import {  Typography, IconButton, Toolbar, Drawer, List, ListItemText, ListItemIcon, ListItemButton} from '@mui/material';
import React, { useState } from 'react'
import { styled, useTheme } from '@mui/material/styles';
import MuiAppBar from '@mui/material/AppBar';

///ICONS
import MenuRoundedIcon from '@mui/icons-material/MenuRounded';
import ArrowBackRoundedIcon from '@mui/icons-material/ArrowBackRounded';

const drawerWidth = 240;

const AppBar = styled(MuiAppBar, {
  shouldForwardProp: (prop) => prop !== 'open',
})(({ theme, open }) => ({
  transition: theme.transitions.create(['margin', 'width'], {
    easing: theme.transitions.easing.sharp,
    duration: theme.transitions.duration.leavingScreen,
  }),
  ...(open && {
    width: `calc(100% - ${drawerWidth}px)`,
    marginLeft: `${drawerWidth}px`,
    transition: theme.transitions.create(['margin', 'width'], {
      easing: theme.transitions.easing.easeOut,
      duration: theme.transitions.duration.enteringScreen,
    }),
  }),
}));

export default function Sidebar() {
  const [open ,setOpen] = useState(true)
  const theme = useTheme();
  return (
// HEADER
  <AppBar position='fixed' open={open} 
  onClick={() => {
    if(open){
      setOpen(false)
    }
  }}>
    

      <Toolbar>

{/*Button for side bar  */}
      <IconButton
            size="large"
            edge="start"
            color="inherit"
            aria-label="menu"
            sx={{ mr: 2, ...(open && { display: 'none' }) }}
           onClick={(e) => {
             e.preventDefault()
             setOpen(true)
           }}>
{/* ICON */}
            <MenuRoundedIcon fontSize='large' />

          </IconButton>
{/* TEXT */}

          <Typography variant="h6" noWrap>
            We are the love sick girl
          </Typography>

          {/* SIDE BAR */}
      <Drawer
      variant="persistent"
      anchor="left"
      open={open}
      
      sx={{
        
        width: drawerWidth,
        flexShrink: 0,
        '& .MuiDrawer-paper': {
          width: drawerWidth,
          boxSizing: 'border-box',
        },
      }}>
        
{/* HEADER */}
      
      <List >

        <ListItemButton variant='contained'>
          
          <ListItemText primary="      "/>

          <ListItemIcon>
            <ArrowBackRoundedIcon variant='filled' fontSize='large' />
          </ListItemIcon>

          
        </ListItemButton>

      </List>
  


      
      </Drawer>

    </Toolbar>

  </AppBar>




   
  )
}
