import { Button, 
  Fab, 
  Grid, 
  TextField, 
  Typography ,
  TableContainer,
  TableRow,
  TableHead,
  TableCell,
  Checkbox,
  Table,
  Paper, 
  TableBody,
Select,
MenuItem,
} from '@mui/material'

import { styled } from '@mui/material/styles';
import {React , useEffect, useState }from 'react';
import axios from 'axios';

import AddIcon from '@mui/icons-material/Add';
import { DataGrid } from '@mui/x-data-grid';




const CTextField = styled(TextField)({
  '& .MuiInputBase-input': {

    position: 'relative',
    fontSize: 20,
    padding: '10px 12px',

  },

})

const Products = () => {

//Data from API
const [data,setData] = useState([])
const [search,setSearch] = useState("")

  useEffect(() => {

    var api = ""

    if (search === ""){
      api = "https://jsonplaceholder.typicode.com/todos"
    }else{
      api = "https://jsonplaceholder.typicode.com/todos?id" + search
    }
    

     axios.get(api)
     .then(res=>setData(res.data))
  });



// Column header
  const columns = [
    { field: 'id', 
      headerName: 'ID', 
      width: 70 ,
      headerAlign: 'center',
      type: "number",
      renderCell: (cellValues) => {
        return (
          <div
            style={{
              color: "black",
              fontSize: 18,
              width: "100%",
              textAlign: "center"
            }}
          >
            {cellValues.value}
          </div>
        );
      }
    
    
    },

    { field: 'title', 
      headerName: 'Name ', 
      width: 200,
      headerClassName: 'super-app-theme--header',
      headerAlign: 'center', 
      type: "string",
      renderCell: (cellValues) => {
        return (
          <div
            style={{
              color: "black",
              fontSize: 18,
              width: "100%",
              textAlign: "center"
            }}
          >
            {cellValues.value}
          </div>
        );
      }
    
    },

    { field: 'userId', 
      headerName: 'Category ', 
      width: 130,
      headerAlign: 'center', 
      renderCell: (cellValues) => {
        return (
          <div
            style={{
              color: "black",
              fontSize: 18,
              width: "100%",
              textAlign: "center"
            }}
          >
            {cellValues.value}
          </div>
        );
      }
  
    
    },

    { field: 'title', 
      headerName: 'Stocks ', 
      width: 130,
      headerAlign: 'center', 
      renderCell: (cellValues) => {
        return (
        <div
        style={{
          color: "black",
          fontSize: 18,
          width: "100%",
          textAlign: "center"
          }}
        >
          {cellValues.value}
          </div>
          );
      }
    },

    { field: 'completed', 
      headerName: 'Price', 
      width: 130,
      headerAlign: 'center', 
      type:"bool",
      renderCell: (cellValues) => {
        return (
          <div
            style={{
              color: "black",
              fontSize: 18,
              width: "100%",
              textAlign: "center"
            }}
          >
            {cellValues.value}
          </div>
        );
      }
    },

  ];

  return (
    <div>

{/* Whole Container  */}
<Grid container spacing={2}>

{/* Products */}
    <Grid container item xs={12} md={10}>
      <Typography variant='h3' marginY={2} marginLeft={2}> Products</Typography>
    </Grid>

{/* Button container style */}
    <Grid container item xs={12} md={2}>

{/* Button container para may margin na maayos */}
      <Grid
        container
        direction="row"
        justifyContent="flex-start"
        alignItems="center"
        margin={2}>
            <Fab color="primary" variant='extended' aria-label="add">
                <AddIcon />
            add item
            </Fab>
      </Grid>

    </Grid>

{/* Search bar and data container */}
    <Grid
      container
      item
      xs={12}
      direction="row"
      justifyContent="center"
      alignItems="flex-start">

      {/* Container para maayos ang margin */}
        <Grid container item xs={12} marginLeft={3} marginRight={3} style={{width: '1000'}}>

          {/* Search bar */}
          <CTextField fullWidth value = {search} onChange={e => setSearch(e.target.value)}  placeholder='Search by product name' variant='standard'/>

          <Grid container item xs={2} md={1} margin={2}>
            <Button variant='contained'>Category</Button>
  
          </Grid>
          <Grid container item xs={2} md={1} margin={2}>
            <Button variant='contained'>Stocks</Button>
          </Grid>

          {/* Datagrid */}
          <Grid
          container
          marginLeft={2}
          marginRight={4}
          marginBottom={3}
          style={{height:550 ,
          width:'100%',
          backgroundColor: 'White',
          border:'2px solid black',
          
          
          }}>

               <DataGrid
              //  {...data}
                  rows={data}
                  columns={columns}
                  pageSize={10}
                  checkboxSelection
                />
    
        </Grid>
      
      </Grid>

    
    </Grid> 
          
</Grid>
    </div>
  )
}

export default Products
