import { Autocomplete, Box, Button,  Grid,  InputAdornment,  TextField, Typography } from '@mui/material'
import { styled } from "@mui/material/styles";
import React, { useState } from 'react'




// Custom TextField

const CTextField = styled(TextField)({
  '& .MuiInputBase-input': {

    position: 'relative',
    fontSize: 20,
    padding: '12px',

  },
  "& label.Mui-focused": {
    color: "#9B3C21"
  },

  "& .MuiOutlinedInput-root": {
    "& fieldset": {
      border: "2px solid #000000",
    },
    "&.Mui-focused fieldset": {

      border: "2px solid #9B3C21"
    }
  }
});


const Add_item = () => {

  const top100Films = [
  //   'The Shawshank Redemption',
  // 'The Godfather','The Godfather: Part II','The Dark Knight',
  // '12 Angry Men',"Schindler's List",'Pulp Fiction'];



    { label: 'The Shawshank Redemption', year: 1994 },
    { label: 'The Godfather', year: 1972 },
    { label: 'The Godfather: Part II', year: 1974 },
    { label: 'The Dark Knight', year: 2008 },
    { label: '12 Angry Men', year: 1957 },
    { label: "Schindler's List", year: 1993 },
    { label: 'Pulp Fiction', year: 1994 }];

const [category,setCategory] = useState([])


  return (
    <div>


      {/* WHOLE CONTAINER LOADS */}
      <Grid 
        container 
        spacing={2} 
        direction="row"
        justifyContent="center"
        alignItems="center">


        {/* Add Item */}

          <Typography marginTop= { 2} variant='h3'> Add new item</Typography>
      

        {/* container inputs */}
        <Grid
          container
          direction="row"
          justifyContent="center"
          alignItems="center">

{/* isa pang container para maayos ang margin */}
          <Grid
          container
          marginY={2}
          marginLeft={5}
          marginRight={2}
          direction="row"
          justifyContent="center"
          alignItems="center">
      
            <Grid container
            margin={2}
            direction="row"
            justifyContent="center"
            alignItems="center"
            style={{
              width:800,
              border: '2px solid black',
              borderRadius: '8px',
              backgroundColor: 'white'
                 }}
            >

          {/* Label Productname */}
              <Grid container  item md={5} justifyContent="center" margin={2}>
                 <Typography  variant='h5'>Product name</Typography>
              </Grid>

          {/* Input Productname*/}
              <Grid item md={6} marginX={1}>
                <CTextField fullWidth type='text' placeholder='Input product Name' variant="outlined" margin="normal"/>
              </Grid>


{/* ////////////////////////////////////////////////////////// */}

  {/* Label Productname */}
              <Grid container item md={5} justifyContent="center" margin={2}>
                 <Typography  variant='h5'>Product Category</Typography>
              </Grid>

  {/* Input Product Category */}
              <Grid item md={6} marginX={1}>


                <Autocomplete
               
                freeSolo
                disablePortal
                options={top100Films.map((option) => option.label)}
                onChange={(event, value) => setCategory(value)}
                sx={{
                  width: 400
                }}
                renderInput={
                  (params) =>

                  // Textfield
                  <CTextField
                  {...params}
                   
                  value={category}
                  onChange={e=>setCategory(e.target.value)}
                  type='text' 
                  placeholder='Input product category'
                  variant="outlined" 
                  margin="normal"/>} />       

            
        
              </Grid>

{/* ////////////////////////////////////////////////////////// */}

     
          {/* Label Productname */}
          <Grid container  item md={5} justifyContent="center" margin={2}>
                 <Typography  variant='h5'>Product Stocks</Typography>
              </Grid>

          {/* Input Productname*/}
              <Grid item md={6} marginX={1}>
                <CTextField fullWidth type='number' placeholder='Input product stocks' variant="outlined" margin="normal"/>
              </Grid>



{/* ////////////////////////////////////////////////////////// */}   

      {/* Label Product price */}
              <Grid container  item md={5} justifyContent="center" margin={2}>
                 <Typography  variant='h5'>Product Price</Typography>
              </Grid>

      {/* Input Product product*/}
              <Grid item md={6} marginTop={1} marginBottom={2}>
                <CTextField 
                fullWidth 
                type='number' 
                placeholder='Input product price' 
                variant="outlined" 
                margin="normal"
                InputProps={{
                  startAdornment: (
                    <InputAdornment position="start">
                      <p>Php</p>
                   
                    </InputAdornment>
                  ),
                }}
                />
              </Grid>
            </Grid>
            


          </Grid>

          <Button 
          variant='contained'  
          style={{
            borderRadius: '10px',
            width:200,
            marginBottom:10,
            padding:10
          }}> create </Button>

            <Button variant='contained'  style={{
            borderRadius: '10px',
            width:200,
            marginBottom:10,
            padding:10
          }}> cancel </Button>

        </Grid>

        













      </Grid>
      
    </div>
  )
}

export default Add_item
