import { Autocomplete, Box, Button,  Grid,  InputAdornment,  TextField, Typography} from '@mui/material'
import { styled } from "@mui/material/styles";
import React, { useEffect, useState } from 'react'
import {useNavigate } from 'react-router-dom';
import axios from 'axios';


import Alert from '@mui/material/Alert';
import Collapse from '@mui/material/Collapse';



// Custom TextField
const CTextField = styled(TextField)({
  '& .MuiInputBase-input': {

    position: 'relative',
    fontSize: 20,
    padding: '12px',

  },
  "& label.Mui-focused": {
    color: "#9B3C21"
  },

  "& .MuiOutlinedInput-root": {
    "& fieldset": {
      border: "2px solid #000000",
    },
    "&.Mui-focused fieldset": {

      border: "2px solid #9B3C21"
    }
  }
});


const Add_item = () => {

  ///Data
  const [Data,setData]= useState([])



const [show_alert,setShow_alert] = useState()

//Json create

const [create, setCreate] = useState({
  "Product_name" : "",
  "Product_category" : "",
  "Product_stocks" : 0,
  "Product_price" : 0

})

useEffect(() => {
  axios.get("http://localhost/REACTJS/cognate1%20api/ProductdataAPI.php")
  .then(res=>setData(res.data))


});

const create_submit = (e) => {
  e.preventDefault()
  axios.post("http://localhost/REACTJS/cognate1 api/Create_productdataAPI.php", create)
  .then(res=>setShow_alert(res.data[0].Result))


}
let nav = useNavigate()
console.log()
  return (
    <div>

      <Collapse in={show_alert}>
        <Alert  severity="success"
           onClose={()=>setShow_alert(false)}>
           Data has been updated
        </Alert>
      </Collapse>





      {/* WHOLE CONTAINER LOADS */}
      <Grid 
        container 
        fixed
        direction="row"
        justifyContent="center"
        alignItems="center">


  {/* Add Item */}

          <Typography marginTop= {15} variant='h3'> Add new item</Typography>
      

  {/* container inputs */}
        <Grid
        item
          container
          direction="row"
          justifyContent="center"
          alignItems="center">

{/* isa pang container para maayos ang margin */}
          <Grid
          container
          marginY={2}
          marginLeft={5}
          marginRight={2}
          direction="row"
          justifyContent="center"
          alignItems="center">
      
            <Grid container
            margin={2}
            direction="row"
            justifyContent="center"
            alignItems="center"
            style={{
              width:800,
              border: '2px solid black',
              borderRadius: '8px',
              backgroundColor: 'white'
                 }}
            >

          {/* Label Productname */}
              <Grid container  item md={5} justifyContent="center" margin={2}>
                 <Typography 
                 variant='h5'
                 >Product name</Typography>
              </Grid>

          {/* Input Productname*/}
              <Grid item md={6} marginX={1}>

              <CTextField 
                fullWidth 
                type='text' 
                placeholder='Input product name' 
                variant="outlined" 
                margin="normal"
                value = {create.Product_name}
                onChange = {e => setCreate({...create, Product_name: e.target.value})}
                />


              </Grid>


{/* ////////////////////////////////////////////////////////// */}

  {/* Label Productname */}
              <Grid container item md={5} justifyContent="center" margin={2}>
                 <Typography  variant='h5'>Product Category</Typography>
              </Grid>

  {/* Input Product Category */}
              <Grid item md={6} marginX={1}>


                <Autocomplete
        
                freeSolo
                disablePortal
                options={Data.map((option) => option.Product_category)}
                value = {create.Product_category}
                defaultValue = {Data[0].map((option) => option.Product_category)}
                onChange = {(e, value) => setCreate({...create, Product_category: value})}
 
                // onChange={(event, value) => setCategory(value)}
                sx={{
                  width: 400
                }}
                renderInput={
                  (params) =>

                  // Textfield
                  <CTextField
                  {...params}
                   
                  value = {create.Product_category}
                  onChange = {e => setCreate({...create, Product_category: e.target.value})}
   
                  type='text' 
                  placeholder='Input product category'
                  variant="outlined" 
                  margin="normal"/>} /> 

        
              </Grid>

{/* ////////////////////////////////////////////////////////// */}

     
          {/* Label Productstocks */}
          <Grid container  item md={5} justifyContent="center" margin={2}>
                 <Typography  variant='h5'>Product Stocks</Typography>
              </Grid>

          {/* Input Productname*/}
              <Grid item md={6} marginX={1}>
                <CTextField 
                fullWidth 
                type='number' 
                placeholder='Input product stocks' 
                variant="outlined" 
                margin="normal"
                value = {create.Product_stocks}
                onChange = {e => setCreate({...create, Product_stocks: e.target.value})}
                />


                
              </Grid>



{/* ////////////////////////////////////////////////////////// */}   

      {/* Label Product price */}
              <Grid container  item md={5} justifyContent="center" margin={2}>
                 <Typography  variant='h5'>Product Price</Typography>
              </Grid>

      {/* Input Product product*/}
              <Grid item md={6} marginTop={1} marginBottom={2}>
                <CTextField 
                fullWidth 
                type='number' 
                placeholder='Input product price' 
                variant="outlined" 
                margin="normal"
                InputProps={{
                  startAdornment: (
                    <InputAdornment position="start">
                      <p>Php</p>
                   
                    </InputAdornment>
                  ),
                }}
                value = {create.Product_price}
                onChange = {e => setCreate({...create, Product_price: e.target.value})}
                />
              </Grid>
            </Grid>
            


          </Grid>

          <Grid item margin={1}>
            
            <Button
            variant='contained'  
            onClick={create_submit}
            style={{
              borderRadius: '10px',
              width:200,
              marginBottom:10,
              padding:10,
           
            }}> create </Button>

          </Grid>

          <Grid item margin={1}>
            <Button 
            variant='contained'  
            onClick={e=>{e.preventDefault(); nav("/Mainpage/Products")}}
            style={{
            borderRadius: '10px',
            width:200,
            marginBottom:10,
            padding:10,
     
          }}> cancel </Button>

          </Grid>

        </Grid>

      </Grid>
      
    </div>
  )
}

export default Add_item
