import { AppBar, Typography, IconButton, Toolbar, Drawer, makeStyles} from '@mui/material';
import React, { useState } from 'react'
import MenuRoundedIcon from '@mui/icons-material/MenuRounded';


const drawerWidth = 240;


export default function Sidebar() {
  const [opem ,setOpem] = useState(false)

  return (
// HEADER
  <AppBar position='fixed'> 

      <Toolbar>

{/*Button for side bar  */}
      <IconButton
            size="large"
            edge="start"
            color="inherit"
            aria-label="menu"
            sx={{ mr: 2, ...(opem && { display: 'none' }) }}
           onClick={(e) => {
             e.preventDefault()
             setOpem(true)
           }}>
{/* ICON */}
            <MenuRoundedIcon />

          </IconButton>
{/* TEXT */}

          <Typography variant="h6" noWrap>
            We are the love sick girl
          </Typography>

          {/* SIDE BAR */}
      <Drawer
      variant="persistent"
      anchor="left"
      open={opem}>
        
        <Typography variant="h6" noWrap>
            We are the love sick girl
          </Typography>



        <IconButton
        size="large"
        edge="start"
        color="inherit"
        aria-label="menu"
        // sx={{ mr: 3 }}
        onClick={
          (e) => {
            e.preventDefault()
            setOpem(false)
            }
          }>
            <MenuRoundedIcon />
        </IconButton>
      
      </Drawer>

    </Toolbar>

  </AppBar>




   
  )
}
