import {  
  Typography, 
  IconButton, 
  Toolbar, 
  Drawer, 
  Grid,
  Box,
  ButtonGroup,
  Button,

} from '@mui/material';

import React, { useState } from 'react'
import { 
  styled, 
} from '@mui/material/styles';

import {  useNavigate } from 'react-router-dom';

import MuiAppBar from '@mui/material/AppBar';

///ICONS

import LOGO  from './icons/user.png'

import MenuRoundedIcon from '@mui/icons-material/MenuRounded';
import AnalyticsOutlinedIcon from '@mui/icons-material/AnalyticsOutlined';
import AddShoppingCartOutlinedIcon from '@mui/icons-material/AddShoppingCartOutlined';
import BusinessCenterOutlinedIcon from '@mui/icons-material/BusinessCenterOutlined';
import AutoGraphOutlinedIcon from '@mui/icons-material/AutoGraphOutlined';
import FeedOutlinedIcon from '@mui/icons-material/FeedOutlined';


const drawerWidth = 240;
  
// Custom Header
const AppBar = styled(MuiAppBar, {
    shouldForwardProp: (prop) => prop !== 'open',
  })(({ theme, open }) => ({
    transition: theme.transitions.create(['margin', 'width'], {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
    ...(open && {
      width: `calc(100% - ${drawerWidth}px)`,
      marginLeft: `${drawerWidth}px`,
      transition: theme.transitions.create(['margin', 'width'], {
        easing: theme.transitions.easing.easeOut,
        duration: theme.transitions.duration.enteringScreen,
      }),
    }),
  }));

  

const Mainpage = () => {



  // NAVIGATION TO ANOTHER PAGE
  let nv = useNavigate()


  //Hook for sidebar true or false
  const [open ,setOpen] = useState(true)


 
// CUSTOM BUTTON
const buttons = [

  // Button Dashboard
  <IconButton  color="secondary" onClick={(e)=>{e.preventDefault(); nv("/Mainpage") }}>
    {/* Icon */}
    <AnalyticsOutlinedIcon  fontSize='large'/> 
    {/* Text */}
    <Typography  variant='h6'marginX={3} >Dashboard</Typography>
  </IconButton>,

  // Button Stocks
  <IconButton fullwidth color="secondary" onClick={(e)=>{e.preventDefault(); nv("/Mainpage/Stocks")}}>
    <AddShoppingCartOutlinedIcon  margin='dense' fontSize='large'/>
    <Typography variant='h6' marginX={5.5} >Stocks</Typography>
  </IconButton>,


    // Button Products
  <IconButton fullwidth color="secondary" onClick={(e)=>{e.preventDefault(); nv("/Mainpage/Products")}}>
    <BusinessCenterOutlinedIcon  margin='dense' fontSize='large'/>
    <Typography variant='h6'marginX={4.2} >Products</Typography>
  </IconButton>,

    // Button Sales Report
  <IconButton fullwidth color="secondary" onClick={(e)=>{e.preventDefault(); nv("/Mainpage/Salesreport")}}>
    <AutoGraphOutlinedIcon  margin='dense' fontSize='large'/>
    <Typography noWrap variant='h6' marginX={2}  >Sales Report</Typography>
  </IconButton>,

    // Button Invoice
  <IconButton fullwidth color="secondary" onClick={(e)=>{e.preventDefault(); nv("/Mainpage/Invoice") }}>
    <FeedOutlinedIcon  margin='dense' fontSize='large'/>
    <Typography variant='h6'marginX={5.5} >Invoice</Typography>
  </IconButton>,

];




  return (
    <div>

<AppBar position='fixed' open={open} 
  onClick={() => {
    if(open){
      setOpen(false)
    }
  }}>
    

      <Toolbar>

{/*Button for side bar  */}
      <IconButton
            size="large"
            edge="start"
            color="inherit"
            aria-label="menu"
            sx={{ mr: 2, ...(open && { display: 'none' }) }}
           onClick={(e) => {
             e.preventDefault()
             setOpen(true)
           }}>
{/* ICON */}
            <MenuRoundedIcon fontSize='large' />

          </IconButton>


{/* SIDE BAR */}
      <Drawer
// permanent
      variant="persistent"
      anchor="left"
      open={open}
      sx={{
        
        width: drawerWidth,
        flexShrink: 0,
        '& .MuiDrawer-paper': {
          width: drawerWidth,
          boxSizing: 'border-box',
        },
      }}>
        
        <Grid           
          item 
  
          container 
          direction="column"
          justifyContent="center"
          alignItems="center">

            {/* Picturebox */}
            <Box
            fixed
            component='img'
            marginY={2}

            sx={{
              width: 100,
              height: 100,
            }}
            src={LOGO} />
            

{/* LABEL */}
            <Typography variant='subtitle1'> Frances Bryan Maneclang</Typography>
            <Typography variant='subtitle2'> CEO</Typography>
  {/* Logout  */}
            <br/>
            <Button variant='outlined' color='secondary' 
            onClick={
              (e)=>{
                e.preventDefault()
                nv('/')
              }
            }> Logout </Button>
            <br/>

        </Grid>



                <ButtonGroup
                fullwidth
                alignItems='flex-start'
                orientation="vertical"
                aria-label="vertical contained button group"
                variant="outlined">


                {buttons}

              </ButtonGroup>
      

      
  
      </Drawer>
    </Toolbar>
  </AppBar>
  
  <br/>
  <br/>
  <br/>
  <br/>
      
     

    </div>
  )
}

export default Mainpage
