import React, { useRef, useState } from 'react';
import emailjs from '@emailjs/browser';
import { Button } from '@mui/material';

const Testuploadimage = () => {
  const form = useRef();
  
  const [send,setSend] = useState(
    {
      from_name: '',
      to_name: '',
      message: ''

    }
  )

  const sendEmail = (e) => {
    e.preventDefault();

    emailjs.sendForm('Gmail', 'template_ctj1ylr', send, 'HygvBj57xmwzHyqWt')
      .then((result) => {
          console.log(result.text);
      }, (error) => {
          console.log(error.text);
      });
  };

  return (
    <form ref={form} onSubmit={sendEmail}>
      <label>from_name</label>
      <input type="text" name="from_name" 
      value={send.from_name}
      onChange={
        e=> setSend({...send, from_name: e.target.value})
      }
      />

      <label>to_name</label>
      <input type="email" name="to_name"
      value={send.to_name}
      onChange={
        e=> setSend({...send, to_name: e.target.value})
      } />

      <label>Message</label>
      <textarea name="message" 
      value={send.message}
      onChange={
        e=> setSend({...send, message: e.target.value})
      }
      />

      <Button onClick={
        sendEmail
      }>
        click me
      </Button>
      <input type="submit" value="Send" />
    </form>
  );
};

export default Testuploadimage
