import { Grid, Box,Paper, Typography, Stack, TextField, InputAdornment, Button, IconButton, Fab, Divider } from '@mui/material'
import { styled } from "@mui/material/styles";

import React, { useState } from 'react'

//ICONS
import EditOutlinedIcon from '@mui/icons-material/EditOutlined';
import BorderColorOutlinedIcon from '@mui/icons-material/BorderColorOutlined';

import LOGO  from '../icons/user.png'
import { useNavigate } from 'react-router-dom';

// Custom TextField

const CTextField = styled(TextField)({
    '& .MuiInputBase-input': {
  
      position: 'relative',
      fontSize: 20,
      padding: '12px',
  
    },
    "& .MuiOutlinedInput-root.Mui-disabled": {
      "&  fieldset": {
        
        borderColor: "#FFFFFF",
        
      },
      "& text": {
        color: "#000000"
      }

    },
    "& label.Mui-focused": {
      color: "#9B3C21"
    },

    "& .MuiOutlinedInput-root": {
      "& fieldset": {
        border: "2px solid #000000",
      },
      "&.Mui-focused fieldset": {
  
        border: "2px solid #9B3C21"
      }
    }
  });

const Edit_profile = () => {

  //Login_Data
  const [edit_data, setEdit_data] = useState({
    "email" :"ako@gmail.com",
    "password" : 1234
  })

  //enable hooks
  const [enble, setEnble] = useState(true)


    // NAVIGATION TO ANOTHER PAGE
    let nv = useNavigate()
  return (
    <div>
        <Grid
        container
        direction="column "
        justifyContent="center"
        alignItems="center">

        <Box padding={2} paddingTop={10}>

            <Grid 
            component={Paper} 
            variant='outlined' 
            padding={3}
            style={{
                backgroundColor:'white',
                border:'2px solid black'
            }}>
                <Stack
                direction="column"
                justifyContent="center"
                alignItems="center"
                spacing={2}>

                  {/* Logo */}
                    <Box 
                    fixed
                    component='img'
                    style={{
                        width:120,
                        height:120,
                    }}
                    src={LOGO}/>

                    {/* Label Name */}
                    <Typography  variant='h4'>Francis Maneclang</Typography>
                    <Typography  variant='h6'>CEO</Typography>
{/* Edit and cancel edit button */}
                    <Grid container justifyContent="flex-end">

                      <IconButton color='primary' onClick={e=>{
                        e.preventDefault(); 
                        setEnble(false)}}>
                        <BorderColorOutlinedIcon fontSize='large' />
                      </IconButton>

                    </Grid>



                    {/* Email edit */}
                    <CTextField 
                    fullWidth 
                    type='email'
                    variant="outlined" 
                    margin="normal"

                    disabled={enble}

                    value = {edit_data.email}
                    onChange = {e => setEdit_data({...edit_data, email: e.target.value})}
                    />
                    {/* password edit */}
                    <CTextField 
                    fullWidth 
                    type='password'
    
                    placeholder='Input password'
                    variant="outlined" 
                    margin="normal"

                    disabled={enble}
                    helperText={enble.password? "click edit button to edit a text" : ""}
                    value = {edit_data.password}
                    onChange = {e => setEdit_data({...edit_data, password: e.target.value})}
                    />

                    {enble === true && <Divider>Click edit button to edit</Divider> }              
            
                    <Button
                    variant='contained'  
                    style={{
                    borderRadius: '20px',
                    width:150,
                    padding:10,
                    }}> Save </Button>

                    <Button 
                    variant='contained' 
                    style={{
                    borderRadius: '20px',
                    width:150,
                    padding:10}}
                    
                    onClick={e=> nv("/Mainpage")}
                    
                    > cancel </Button>

      


                </Stack>
              




              


            




            </Grid>
       
                

            


         
    
        </Box>
         
        </Grid>
    </div>
  )
}

export default Edit_profile
