import { Fab, Grid , Typography, Box, Button, FormControlLabel, Switch, Alert, Collapse, Radio, RadioGroup} from '@mui/material'
import Dialog, { DialogProps } from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import DialogTitle from '@mui/material/DialogTitle';
import React, { useState } from 'react'
import { useNavigate } from 'react-router-dom';
import { CcTextField } from '../Stocks';

// ICONS
import ClearIcon from '@mui/icons-material/Clear';

import LOGO  from '../../icons/user.png'
import axios from 'axios';

const Add_data = () => {
// Routes
  let NavS = useNavigate();

  // Status
  const [status,setStatus] = useState({
    "status" : "pending",
     "check" : false
  })

  //JSON
  const [data, setData] = useState({
    "Customer_name": "",
    "Customer_email": "",
    "Customer_cell": "",
    "Customer_address": "",
    "Customer_purchase": "",
    "Customer_date": "",
    "Customer_total": 0,
    "Customer_payment":  ""
  })
// Show success
const [show_success,setShow_success] = useState()

// Show error
const [show_error,setShow_error] = useState()

//Dialog show

const add_customerdata = e => {

  e.preventDefault()  

       console.log(data)
  axios.post("http://localhost/REACTJS/cognate1%20api/Create_CustomerdataAPI.php", data)
  .then(res => 
    {
      setShow_success(res.data[0].Result)
      setShow_error(res.data[0].Result)
    })
   
  }


  

  return ( <div>

  {/* Message Dialog */}
  
 
{/* // Whole Container */}
         {/* Alert Success */}
         <Collapse in={show_success}>
         <Alert severity="success"
         onClose={()=>{
           
           setShow_success(false) 
           setData({

            Customer_name: "",
            Customer_email: "",
            Customer_cell: "",
            Customer_address: "",
            Customer_purchase: "",
            Customer_date: "",
            Customer_total: 0,
            Customer_payment:  ""


           })
         }}
         >
           Data has been updated
         </Alert>
       </Collapse>

{/* Alert Error */}
<Collapse fullWidth in={show_error ? false : true}>
         <Alert severity="error"
         onClose={()=>{
           
           setShow_error(true) 
         }}
         >
           ATTENTION! textfield should not be empty!
         </Alert>
       </Collapse>

    <Grid container padding={6} paddingTop={2} >
    


    

{/* Container para sa button paar may padding */}
      <Grid container padding={2}>

               
        {/* Button cancel */}
        <Fab 
        color="primary"  
        aria-label="add" 
        onClick={e=>NavS("/Mainpage/Invoice")} 
        >
          <ClearIcon />
 
        </Fab>

      </Grid>

{/* Container for all components */}
      <Grid 
      container
      direction="row"
      justifyContent="center"
      alignItems="flex-start" 
      style={{
        backgroundColor:"white",
        border:"2px solid black",
        borderRadius: 5
      }}>

{/* CONTAINER FOR ICON LOGO */}
        <Grid item md={5} xs={12}>

          <Grid 
          container 
          padding={2}
          paddingTop={6}
          direction="row"
          justifyContent="center"
          alignItems="flex-start">

          {/* Picturebox */}
              <Box
              fixed
              component='img'
              marginY={2}

              sx={{
                width: 160,
                height: 160,
              }}
              src={LOGO} />

              <Grid md={12} padding={2}>
              {/* Name */}
              <Typography variant='h6'> Name:</Typography>

              <CcTextField 
              fullWidth 
              margin='normal'
              type='text'
              value={data.Customer_name}
              onChange={e=> setData({...data, Customer_name: e.target.value})}
              
              />

              {/* Email */}
              <Typography variant='h6'> Email:</Typography>

              <CcTextField  
              fullWidth 
              margin='normal'
              type='text'
              value={data.Customer_email}
              onChange={e=> setData({...data, Customer_email: e.target.value})}
              />

              {/* Cellphone number */}
              <Typography variant='h6'> Cellphone number:</Typography>
              
              <CcTextField  
              fullWidth 
              margin='normal'
              type='number'              
              value={data.Customer_cell}
              onChange={e=> setData({...data, Customer_cell: String(e.target.value)})}
              />

              
              {/* Address */}
              <Typography variant='h6'> Address:</Typography>
              <CcTextField  
              fullWidth 
              margin='normal' 
              multiline
              type='text'
              value={data.Customer_address}
              onChange={e=> setData({...data, Customer_address: String(e.target.value)})}
              
              /> 


            </Grid>

          </Grid>

          

        </Grid>


        {/* Components details */}
        <Grid container item md={7} xs={12} padding={2} paddingTop={13}>
          
{/* Purchase product */}
              <Typography variant='h6'> Purchase product:</Typography>
              
              <CcTextField  
              fullWidth 
              multiline 
              margin='normal' 
              placeholder='please input the item ID here'
              type='text'
              value={data.Customer_purchase}
              onChange={e=> setData({...data, Customer_purchase: String(e.target.value)})}
              
              />

{/* Date */}
              <Typography variant='h6'> Date:</Typography>
              
              <CcTextField  
              fullWidth 
              margin='normal'
              type='text'
              value={data.Customer_date}
              onChange={e=> setData({...data, Customer_date: String(e.target.value)})}
              
              />

{/* Total product */}
              <Typography variant='h6'> Total:</Typography>
              
              <CcTextField  
              fullWidth 
              margin='normal'
              type='number'
              value={data.Customer_total}
              onChange={e=> setData({...data, Customer_total: String(e.target.value)})}
              
              />

{/* Toggle Button for payment status */}
              <FormControlLabel  
              label= {status.check ? "Paid" : "Pending"} 
              control={
              <Switch 
              checked={status.check} 
              onChange={()=> 
                {
                  setStatus({...status, check: !status.check})
                  setData({...data,Customer_payment: "Paid"})
                  
                }
              }/>
              } />
    <RadioGroup
        row
        aria-labelledby="demo-controlled-radio-buttons-group"
        name="controlled-radio-buttons-group"
       // value={value}
        //onChange={handleChange}
        defaultChecked
      >
        <FormControlLabel value="Paid" control={<Radio />} label="Paid" />
        <FormControlLabel value="Pending  " control={<Radio />} label="Pending" />
      </RadioGroup>

        </Grid>



        {/* Button cancel */}

        <Grid
        item
        container
        md={12}
        padding={5}
        direction="row"
        justifyContent="center"
        alignItems="flex-start">

        {/* Add item button */}
          <Button 
          variant='contained' 
          size='large' 
          style={{
            borderRadius: 50
          }}
          onClick={add_customerdata}>
            Add item
          </Button>

        </Grid>

   

      </Grid>

    </Grid>
    </div>
)
}

export default Add_data
