import { 
  Fab, 
  Grid, 
  IconButton, 
  MenuItem, 
  Select, 
  TextField, 
  Typography ,

} from '@mui/material'

import { styled } from '@mui/material/styles';
import {React , useEffect, useState }from 'react';
import axios from 'axios';

import AddIcon from '@mui/icons-material/Add';
import DeleteOutlineOutlinedIcon from '@mui/icons-material/DeleteOutlineOutlined';

//npm i @mui/x-data-grid
import { DataGrid } from '@mui/x-data-grid';




const CTextField = styled(TextField)({
  '& .MuiInputBase-input': {

    position: 'relative',
    fontSize: 20,
    padding: '10px 12px',

  },

})

const Products = () => {
// Category data variable
const [category, setCategory] = useState("None")

//Data from API
const [data,setData] = useState([]) 

//search data variable
const [search,setSearch] = useState("") 

// Filtering data 
const filtered = !search ? 
data 
: data.filter((person) => 
    person.title.includes(search)
  );


  // http request api
useEffect(() => {

     axios.get('https://jsonplaceholder.typicode.com/todos')
     .then(res=>setData(res.data))
  });



// Column header

  const columns = [

    // ID Column
    { field: 'id', 
      headerName: 'ID', 
      width: 70 ,
      headerAlign: 'center',
      type: "number",
      renderCell: (cellValues) => {
        return (
          <div
            style={{
              color: "black",
              fontSize: 18,
              width: "100%",
              textAlign: "center"
            }}
          >
            {cellValues.value}
          </div>
        );
      }
    },

    // ID Column
    { field: 'title', 
      headerName: 'Name ', 
      width: 200,
      headerClassName: 'super-app-theme--header',
      headerAlign: 'center', 
      type: "string",
      renderCell: (cellValues) => {
        return (
          <div
            style={{
              color: "black",
              fontSize: 18,
              width: "100%",
              textAlign: "center"
            }}
          >
            {cellValues.value}
          </div>
        );
      }
    
    },

    { field: 'userId', 
      headerName: 'Category ', 
      width: 130,
      headerAlign: 'center', 
      renderCell: (cellValues) => {
        return (
          <div
            style={{
              color: "black",
              fontSize: 18,
              width: "100%",
              textAlign: "center"
            }}
          >
            {cellValues.value}
          </div>
        );
      }
  
    
    },

    { field: 'title', 
      headerName: 'Stocks ', 
      width: 130,
      headerAlign: 'center', 
      renderCell: (cellValues) => {
        return (
        <div
        style={{
          color: "black",
          fontSize: 18,
          width: "100%",
          textAlign: "center"
          }}
        >
          {cellValues.value}
          </div>
          );
      }
    },

    { field: 'completed', 
      headerName: 'Price', 
      width: 130,
      headerAlign: 'center', 
      type:"bool",
      renderCell: (cellValues) => {
        return (
          <div
            style={{
              color: "black",
              fontSize: 18,
              width: "100%",
              textAlign: "center"
            }}
          >
            {cellValues.value}
          </div>
        );
      }
    },

    {
      field: 'action',
      headerName: 'Action',
      width: 130,
      headerAlign: 'center', 
      renderCell: () => {
        return (
          <div
            style={{
              width: "100%",
              textAlign: "center",
              // marginRight: '2'
            }}
          >
           

          <IconButton color="primary" aria-label="delete">
              <DeleteOutlineOutlinedIcon/>
          </IconButton>
           
  
          </div>


        );
      }
    }

  ];

  return (
    <div>

{/* Whole Container  */}
<Grid container spacing={2}>

{/* Products */}
    <Grid container item xs={12} md={10}>
      <Typography variant='h3' marginY={2} marginLeft={2}> Products</Typography>
    </Grid>

{/* Button container style */}
    <Grid container item xs={12} md={2}>

{/* Button container para may margin na maayos */}
      <Grid
        container
        direction="row"
        justifyContent="flex-start"
        alignItems="center"
        margin={2}>

          {/* Add item button */}
            <Fab color="primary" variant='extended' aria-label="add">
                <AddIcon />
            add item
            </Fab>


      </Grid>

    </Grid>

{/* Search bar and data container */}
    <Grid
      container
      item
      xs={12}
      direction="row"
      justifyContent="center"
      alignItems="flex-start">

      {/* Container para maayos ang margin */}
        <Grid container item xs={12} marginLeft={3} marginRight={3} style={{width: '1000'}}>

   {/* Search bar */}
          <CTextField 
          fullWidth 
          value = {search} 
          onChange={e => {
            setSearch(e.target.value)
          
          }}  
          placeholder='Search by product name' 
          variant='standard'/>

   {/* CATEGORY STOCKS */}
          <Grid container item xs={2} md={1} margin={2}>
          <Select
          value={category}
          label="Category"
          
        >
          <MenuItem value={10}>Ten</MenuItem>
          <MenuItem value={20}>Twenty</MenuItem>
          <MenuItem value={30}>Thirty</MenuItem>
        </Select>

          </Grid>
         
          {/* Datagrid */}
          <Grid
          container
          marginLeft={2}
          marginRight={4}
          marginBottom={3}
          style={{height:550 ,
          width:'100%',
          backgroundColor: 'White',
          border:'2px solid black',
          
          
          }}>

               <DataGrid
              //  {...data}
                  disableSelectionOnClick
                  rows={filtered} // data na pinasok yung data 
                  columns={columns}
                  pageSize={10}
                  checkboxSelection={true} 
                />
    
        </Grid>
      
      </Grid>

    
    </Grid> 
          
</Grid>
    </div>
  )
}

export default Products
