import { 
  Fab, 
  Grid , 
  Typography, 
  Box, 
  Button, 
  FormControlLabel, 
  Alert, 
  Collapse, 
  Radio, 
  RadioGroup, 
  Select, 
  Chip, 
  MenuItem, 
  Checkbox, 
  ListItemText,
  TextField,
  Autocomplete,
} from '@mui/material'
import Dialog, { DialogProps } from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import DialogTitle from '@mui/material/DialogTitle';
import React, { useEffect, useState } from 'react'
import { useNavigate } from 'react-router-dom';
import { CcTextField } from '../Stocks';
import AdapterDateFns from '@mui/lab/AdapterDateFns';
import LocalizationProvider from '@mui/lab/LocalizationProvider';
import MobileDatePicker from '@mui/lab/MobileDatePicker'
import moment from 'moment'
// ICONS
import ClearIcon from '@mui/icons-material/Clear';
import AddShoppingCartOutlinedIcon from '@mui/icons-material/AddShoppingCartOutlined';

import LOGO  from '../../icons/user.png'
import axios from 'axios';
import { DataGrid } from '@mui/x-data-grid';




const Add_data = () => {
// Routes
  let NavS = useNavigate();


// Date ITO
const [value,setValue] = useState(
  new Date()
)
  //JSON
  const [data, setData] = useState({
    "Customer_name": "",
    "Customer_email": "",
    "Customer_cell": "",
    "Customer_address": "",
    "Customer_purchase": "",
    "Customer_date": new Date(),
    "Customer_total": 0,
    "Customer_payment":  "Paid"
  })
// Show success
const [show_success,setShow_success] = useState()

// Show error
const [show_error,setShow_error] = useState(true)

// Show error show
const [show_err,setShow_err] = useState()

// Product purchase
const [selectname, setSelectname] = useState([])

const [datadata,setDatdata] = useState([])

// Menu props
const ITEM_HEIGHT = 48;
const ITEM_PADDING_TOP = 8;

const MenuProps = {
  PaperProps: {
    style: {
      maxHeight: ITEM_HEIGHT * 4.5 + ITEM_PADDING_TOP,
      width: 250,
    },
  },
};

// Message if data exist
const [Msg,setMsg] = useState()

// Add customer data
const add_customerdata = e => {

  e.preventDefault()  

  
  axios.post("http://localhost/REACTJS/cognate1%20api/Create_CustomerdataAPI.php", {
    "Customer_name": data.Customer_name,
    "Customer_email": data.Customer_email,
    "Customer_cell": data.Customer_cell,
    "Customer_address": data.Customer_address,
    "Customer_purchase": selectname.toString(),
    "Customer_date": moment(value,"mm-dd-yyyy").format().split('T')[0],
    "Customer_total": data.Customer_total,
    "Customer_payment":  data.Customer_payment
  })
  .then(res => 
    {
      setShow_success(res.data[0].Result)
      setShow_error(res.data[0].Result)
      setShow_err(res.data[0].Result)
      setMsg(res.data[0].Message)
    })
    setDisble(true)

  }

// Data product
const [dataproduct,setDataproduct] = useState([])

// filtered fetch product name
const name = !dataproduct ? 
"empty" 
: dataproduct.map(name=>name.Product_name )

//Product name Proouct


// fetch name 
useEffect(() => {
 axios.get('http://localhost/REACTJS/cognate1%20api/ProductdataAPI.php')
 .then(res=> setDataproduct(res.data))
})

const [num,setNum] = useState("")

const columns = [

  // ID Column
  { field: 'Product_ID', 
    headerName: 'ID', 
    width: 70 ,
    headerAlign: 'center',

    renderCell: (cellValues) => {
      return (
        <div
          style={{
            color: "black",
            fontSize: 18,
            width: "100%",
            textAlign: "center"
          }}
        >
          {cellValues.value}
        </div>
      );
    }
  },

  // ID Column
  { field: 'Product_name', 
    headerName: 'name', 
    width: 200,
    headerClassName: 'super-app-theme--header',
    headerAlign: 'center', 

    renderCell: (cellValues) => {
      return (
        <div
          style={{
            color: "black",
            fontSize: 18,
            width: "100%",
            textAlign: "center"
          }}
        >
          {cellValues.value}
        </div>
      );
    }
  
  },


  { field: 'Product_price', 
    headerName: 'price', 
    width: 130,
    headerAlign: 'center', 

    renderCell: (cellValues) => {
      return (
        <div
          style={{
            color: "black",
            fontSize: 18,
            width: "100%",
            textAlign: "center"
          }}
        >
          {cellValues.value}
        </div>
      );
    }
  },
  
];

const [row,setRow] = useState([])

const [disble,setDisble] = useState(false)

  return ( <div>

  {/* Message Dialog */}
  
 
{/* // Whole Container */}
         {/* Alert Success */}
         <Collapse in={show_success}>
         <Alert severity="success"
         onClose={()=>{
           
           setShow_success(false) 
           setData({

            Customer_name: "",
            Customer_email: "",
            Customer_cell: "",
            Customer_address: "",
            Customer_purchase: [""],
            Customer_date: new Date(),
            Customer_total: 0,
            Customer_payment:  "Paid"


           })
           setSelectname([])
           setDisble(false)
         }}
         >
           Data has been updated
         </Alert>
       </Collapse>

{/* Alert Error */}
<Collapse fullWidth in={show_err ? false : true}>
         <Alert severity="error"
         onClose={()=>{
           
           setShow_err(true) 
           setShow_error(true)
           setDisble(false)
         }}
         >
           ATTENTION! textfield should not be empty!
         </Alert>
       </Collapse>

    <Grid container padding={6} paddingTop={2} >
    


    

{/* Container para sa button paar may padding */}
      <Grid container padding={2}>

               
        {/* Button cancel */}
        <Fab 
        color="primary"  
        aria-label="add" 
        onClick={e=>NavS("/Mainpage/Invoice")} 
        >
          <ClearIcon />
         
        </Fab>
        <Typography variant='h3'  marginLeft={2}> Add Data</Typography>
      </Grid>

{/* Container for all components */}
      <Grid 
      container
      direction="row"
      justifyContent="flex-start"
      alignItems="flex-start" 
      style={{
        backgroundColor:"white",
        border:"2px solid black",
        borderRadius: 5
      }}
      onClick={e=>
        {
          setData({...data,Customer_purchase: selectname.toString()})
        }
      }
     >

{/* Name textfield */}
        <Grid item md={10} padding={2}>
            <CcTextField label='Name' type='text' fullWidth />
        </Grid>

{/* Email textfield */}
        <Grid item md={10} padding={2}>
            <CcTextField label='Email Address' type='Email' fullWidth/>
        </Grid>   

{/* Contact textfield */}
        <Grid item md={10} padding={2}>
            <CcTextField label='Contact Number' type='text' fullWidth/>
        </Grid>

{/* Address textfield */}
        <Grid item md={12} padding={2}>
            <CcTextField multiline label='Address' type='text' fullWidth/>
        </Grid> 

  {/* Date textfield */}
        <Grid item md={2} padding={2}>
            <LocalizationProvider dateAdapter={AdapterDateFns}>
                <MobileDatePicker
                error={show_error? false : true}
                inputFormat="MM/dd/yyyy"
                value={value}
                onChange={(newValue) => setValue(newValue) }
                renderInput={(params) => <CcTextField  {...params} 
                onClick={()=>{
                  setShow_error(true)
                }}
                />}
          
                />

                </LocalizationProvider>

        
        </Grid> 

  {/* Radio Button for payment status */}
        <Grid item md={6} padding={2}>
        
            <RadioGroup
              row
              value={data.Customer_payment}
              onChange={e => {
              setData({...data,Customer_payment: e.target.value })
              }}
            >
              <FormControlLabel  value="Paid" control={<Radio />} label="Paid" />
              <FormControlLabel value="Pending" control={<Radio />} label="Pending" />
            </RadioGroup>

        </Grid>




{/* Customer item purchase detail */}
        <Grid item md={12} padding={4}>



          <Grid 
          container
          direction="row"
          justifyContent="flex-start"
          alignItems="center" 
          style={{
            border: "2px solid #9B3C21",
            borderRadius:'10px'
          }} 
          paddingBottom={2}
          spacing={2}>

{/* Add purchase row */}
            <Grid item md={12}>
             
              <Fab 
                color='primary'
                aria-label="add" 
                onClick={e=>NavS("/Mainpage/Invoice")} 
                size='small'
                variant='extended'
                >
                <AddShoppingCartOutlinedIcon />      
                add item  
              </Fab>
            </Grid>

{/* Customer purchase */}
            <Grid item md={4}>
              <CcTextField label="customer purchase" fullWidth/>
            </Grid>

{/* Customer price */}         
            <Grid item md={2}>
              <CcTextField label="price" fullWidth/>
            </Grid>

{/* Customer quantity */}   
            <Grid item md={2}>
              <CcTextField label="quantity" fullWidth/>
            </Grid>

{/* Customer total  */}   
            <Grid item md={2}>
              <CcTextField label="total price" fullWidth/>
            </Grid>

{/* Button remove */} 
            <Grid item md={2}>
              <Fab 
                color="primary"  
                aria-label="add" 
                onClick={e=>NavS("/Mainpage/Invoice")} 
                size='small'
                >
                    <ClearIcon />
         
              </Fab>
            </Grid>



      
            
          </Grid>

    
       
        </Grid>



      </Grid>

    </Grid>
    </div>
)
}

export default Add_data
