// Library components

import { Grid, 
        InputAdornment,
        Typography,
        Fab,
        IconButton,
        Collapse,
        Button,
        FormControlLabel,
        Alert,
        RadioGroup,
        Radio,
        Tooltip,
        Dialog,
        DialogTitle,
        DialogContent,
        DialogContentText,
        DialogActions,

} from '@mui/material'
import { CTextField } from '../products/Products' 

import React, { useEffect, useState } from 'react'


// Icons
import SearchOutlinedIcon from '@mui/icons-material/SearchOutlined';
import AddIcon from '@mui/icons-material/Add';
import DeleteOutlineOutlinedIcon from '@mui/icons-material/DeleteOutlineOutlined';
import moment from 'moment'
import ModeEditOutlineRoundedIcon from '@mui/icons-material/ModeEditOutlineRounded';


// Routes
import { useNavigate } from 'react-router-dom';
import { DataGrid } from '@mui/x-data-grid';
import axios from 'axios';



// Transfer data to edit data
export var dataT = {}





const Invoice = () => {
// Navigation
let NavS = useNavigate();




// Column header
const columns = [

  // ID Column
  { field: 'Customer_ID', 
    headerName: 'ID', 
    width: 70 ,
    headerAlign: 'center',

    renderCell: (cellValues) => {
      return (
        <div
          style={{
            color: "black",
            fontSize: 18,
            width: "100%",
            textAlign: "center"
          }}
        >
          {cellValues.value}
        </div>
      );
    }
  },

  // name Column
  { field: 'Customer_name', 
    headerName: 'name', 
    width: 200,
    headerClassName: 'super-app-theme--header',
    headerAlign: 'center', 

    renderCell: (cellValues) => {
      return (
        <div
          style={{
            color: "black",
            fontSize: 18,
            width: "100%",
            textAlign: "center"
          }}
        >
          {cellValues.value}
        </div>
      );
    }
  
  },



  { field: 'Customer_email', 
    headerName: 'email', 
    width: 130,
    headerAlign: 'center', 

    renderCell: (cellValues) => {
      return (
      <div
      style={{
        color: "black",
        fontSize: 18,
        width: "100%",
        textAlign: "center"
        }}
      >
        {cellValues.value}
        </div>
        );
    }
  },

  { field: 'Customer_cell', 
    headerName: 'Cellphone number', 
    width: 150,
    headerAlign: 'center', 

    renderCell: (cellValues) => {
      return (
        <div
          style={{
            color: "black",
            fontSize: 18,
            width: "100%",
            textAlign: "center"
          }}
        >
          {cellValues.value}
        </div>
      );
    }
  },


{ field: 'Customer_date', 
headerName: 'date', 
width: 150,
headerAlign: 'center', 

renderCell: (cellValues) => {
  return (
    <div
      style={{
        color: "black",
        fontSize: 18,
        width: "100%",
        textAlign: "center"
      }}
    >
      {cellValues.value}
    </div>
  );
}
},

{ field: 'Customer_total', 
headerName: 'total payment', 
width: 130,
headerAlign: 'center', 

renderCell: (cellValues) => {
  return (
    <div
      style={{
        color: "black",
        fontSize: 18,
        width: "100%",
        textAlign: "center"
      }}
    >
      {cellValues.value}
    </div>
  );
}
},

{ field: 'Customer_payment', 
headerName: 'status', 
width: 130,
headerAlign: 'center', 

renderCell: (cellValues) => {
  return (
    <div
      style={{
        color: "black",
        fontSize: 18,
        width: "100%",
        textAlign: "center"
      }}
    >
      {cellValues.value}
    </div>
  );
}
},
  
// delete
  {
    field: 'action',
    headerName: 'Delete',
    width: 130,
    headerAlign: 'center', 
    renderCell: () => {
      return (
        <div
          style={{
            width: "100%",
            textAlign: "center",
            // marginRight: '2'
          }}
        >
         

        <IconButton color="primary" aria-label="delete">
            <DeleteOutlineOutlinedIcon/>
        </IconButton>
         
        </div>


      );
    }
  },
  // edit
  {
    field: 'View',
    headerName: 'View',
    width: 130,
    headerAlign: 'center', 
    renderCell: () => {
      return (
        <div
          style={{
            width: "100%",
            textAlign: "center",
            // marginRight: '2'
          }}
        >
         

        <IconButton color="primary" aria-label="delete">
            <ModeEditOutlineRoundedIcon/>
        </IconButton>
         
        </div>


      );
    }
  }

];
//search data variable
const [search,setSearch] = useState("")

// Data 
const [Data,set] = useState()



// Status
const [stat, setStat] = useState("Paid")

// const data_stat = Data.filter(person=>person.Customer_payment === stat )

// Filtering data 
const filtered = !search ? 
Data 
: Data.filter((person) => 
    person.Customer_name.toLowerCase().includes(search.toLowerCase())
  );


// Show success
const [show_success,setShow_success] = useState()

// Show error
const [show_error,setShow_error] = useState()


// Htttp get request
useEffect(() => {
  axios.get('http://localhost/REACTJS/cognate1%20api/CustomerdataAPI.php')
.then(res=>set(res.data.filter(person=>
  
    person.Customer_payment === stat 
  
)))
.catch(err=>console.log(err)) 
})


  // Delete customerdata
const delete_customerdata = (param) => {
  
    if(param.field === "action")
    {
      axios.post("http://localhost/REACTJS/cognate1%20api/Delete_CustomerdataAPI.php", { "Customer_ID": param.id } )
      .then(res => console.log(res.data))
    }
    if (param.field === "View")
    {
      edit_customerdata(param)
      NavS("/Mainpage/Invoice/EditData")
      
    }
}

 // Edit customerdata
const edit_customerdata = (param) => {
  dataT = {
    Customer_ID : param.id,
// Customer name
    Customer_name: String(filtered.filter(index=>index.Customer_ID === param.id)
    .map(ge=>ge.Customer_name)),

// Customer email
    Customer_email: String(filtered.filter(index=>index.Customer_ID === param.id)
    .map(ge=>ge.Customer_email)),

// Customer cell
    Customer_cell: String(filtered.filter(index=>index.Customer_ID === param.id)
    .map(ge=>ge.Customer_cell)),

// Customer address
    Customer_address: String(filtered.filter(index=>index.Customer_ID === param.id)
    .map(ge=>ge.Customer_address)),

// Customer purchase
    Customer_purchase: String(filtered.filter(index=>index.Customer_ID === param.id)
    .map(ge=>ge.Customer_purchase)),

// Customer date
    Customer_date: Date(filtered.filter(index=>index.Customer_ID === param.id)
    .map(ge=>ge.Customer_date)),

// Customer total
    Customer_total: parseInt(filtered.filter(index=>index.Customer_ID === param.id)
    .map(ge=>ge.Customer_total)),

// Customer quantity
    Customer_Quantity: String(filtered.filter(index=>index.Customer_ID === param.id)
    .map(ge=>ge.Customer_Quantity)),

// Customer modeofpayment
    Customer_modepayment: String(filtered.filter(index=>index.Customer_ID === param.id)
    .map(ge=>ge.Customer_modepayment)),

// Customer payment
    Customer_payment: String(filtered.filter(index=>index.Customer_ID === param.id)
    .map(ge=>ge.Customer_payment)),
  }


}


  return (
    <div>
{/* Alert Success */}
      <Collapse in={show_success}>
          <Alert severity="success"
          onClose={()=>{
            
            setShow_success(false) 
          }}
          >
            Data has been updated
          </Alert>
        </Collapse>

{/* Alert Error */}
<Collapse in={show_error ? false : true}>
          <Alert severity="error"
          onClose={()=>{
            
            setShow_error(true) 
          }}
          >
            ATTENTION! textfield should not be empty!
          </Alert>
        </Collapse>


      {/* Whole Container */}
        <Grid container>


        <Dialog
        open={true}
        onClose={false}

      >
        <DialogTitle>
          {"High Resolution"}
        </DialogTitle>

        <DialogContent>
          <DialogContentText>
              are you sure you want delete this ?
          </DialogContentText>
        </DialogContent>

        <DialogActions>
          <Button variant='contained' >Disagree</Button>
          <Button  autoFocus>
            Agree
          </Button>
        </DialogActions>
      </Dialog>



        {/* Stocks */}
        <Grid  item xs={12} md={10}>
                <Typography variant='h3' marginY={2} marginLeft={2}> Invoice</Typography>
            </Grid>
        
{/* Search bar container */}
          <Grid item md={8} xs={12}>

          

            {/* Para may padding */}
           <Grid 
           container 
           padding={3}
           direction="row"
           justifyContent="center"
           alignItems="flex-start">

             {/* Search bar */}
              <CTextField 
              variant='standard' 
              fullWidth 
              placeholder='Searchbar'
              value={search}
              onChange={e=>setSearch(e.target.value)}

              InputProps={{
                endAdornment: (
                  <InputAdornment position="start">
    
                    <SearchOutlinedIcon fontSize='large' />
                 
                  </InputAdornment>
                ),
              }}
              />

          
           </Grid>
         
    
          </Grid>

 {/* add button */}
          <Grid item md={2} xs={7} padding={3}>

              <Tooltip title="Add data">

              {/* Button add */}
                <Fab 
                color="primary" 
                aria-label="add" 
                variant='extended'
                onClick={e=>NavS("/Mainpage/Invoice/AddData")} 
                >
                  <AddIcon />
                  Add Item
                </Fab>
              </Tooltip>
          </Grid>


 {/* Show draft button */}
          <Grid item md={2} xs={7} padding={3}>

              <Tooltip title="Draft list">

              {/* Button add */}
                <Fab 
                color="primary" 
                aria-label="add" 
                onClick={e=>NavS("/Mainpage/Invoice/AddData")} 
                variant='extended'
                >
                  <AddIcon />
                  Draft List
                </Fab>
              </Tooltip>
          </Grid>

{/* Status */}
          <Grid item md={12}>
              {/* Container para may padding */}
            <Grid 
            container 
            paddingLeft={5}
            direction="row"
            justifyContent="flex-start"
            alignItems="center">

              {/* Radio Button for payment status */}
              <RadioGroup
              row
              value={stat}
              onChange={e => {
                setStat(e.target.value)
              }}
            >
              <FormControlLabel  value="Paid" control={<Radio />} label="Paid" />
              <FormControlLabel value="Pending" control={<Radio />} label="Pending" />
            </RadioGroup>


            </Grid>
          </Grid>
          
              

          {/* Container for table */}
          <Grid item md={12}>
            
            {/* Para may padding */}
            <Grid 
            container             
            direction="row"
            justifyContent="flex-start"
            alignItems="flex-start"
            padding={5}>

              {/* pang size at background color */}
              <Grid 
              container
              style={{
                height:550 ,
                width:'100%',
                backgroundColor: 'White',
                border:'2px solid black',
                borderRadius: 3               
              }}
              >



                {/* Data table */}
                <DataGrid
                columns={columns}
                getRowId={(rows)=> rows.Customer_ID}
                rows={filtered} // data na pinasok yung data 
                  
                pageSize={10}
                //delete_customerdata
                onCellClick={delete_customerdata}
                />

              </Grid>
           
          
            </Grid>

          </Grid>

 
        </Grid>

  
    </div>
  )
}

export default Invoice
