import { Button, Collapse, Divider, Grid, InputAdornment, TextField, Toolbar, Typography } from '@mui/material'
import { Box } from '@mui/system'
import { DataGrid } from '@mui/x-data-grid'
import React, { useEffect, useState } from 'react'
import { CTextField } from './products/Products'

import SearchOutlinedIcon from '@mui/icons-material/SearchOutlined';
import axios from 'axios'

const Stocks = () => {

//Consta for table
const [list_data, setList_data] = useState(false)


const columns = [

  // ID Column
  { field: 'Product_ID', 
    headerName: 'ID', 
    width: 70 ,
    headerAlign: 'center',

    renderCell: (cellValues) => {
      return (
        <div
          style={{
            color: "black",
            fontSize: 18,
            width: "100%",
            textAlign: "center"
          }}
        >
          {cellValues.value}
        </div>
      );
    }
  },

  // Product Name
  { field: 'Product_name', 
    headerName: 'name', 
    width: 200,
    headerClassName: 'super-app-theme--header',
    headerAlign: 'center', 

    renderCell: (cellValues) => {
      return (
        <div
          style={{
            color: "black",
            fontSize: 18,
            width: "100%",
            textAlign: "center"
          }}
        >
          {cellValues.value}
        </div>
      );
    }
  
  },
];
//search data variable
const [search,setSearch] = useState("") 


// Filtering data 
const filtered = !search ? 
Data 
: Data.filter((person) => 
    person.Product_name.toLowerCase().includes(search.toLowerCase())
  );

//Data
const [Data,setData] = useState() 

  // http request api
  useEffect(() => {

    axios.get('http://localhost/REACTJS/cognate1%20api/ProductdataAPI.php')
    .then(res=>setData(res.data))
    .catch(err=>console.log(err))
 });

  return (
    <div>

      <Grid container>

        {/* Search bar */}
        <Grid item md={12} padding={2}>
          
          <Grid
          container
          direction="row"
          justifyContent="center"
          alignItems="center"
          style={{
            backgroundColor:'white',
            border: '2px solid black',
            borderRadius: '5px'
          }}
          padding={2}>

         {/* Search bar */}
          <CTextField 
          fullWidth 
          value = {search} 
          onChange={e => setSearch(e.target.value)}  
          placeholder='Search by product name' 
          variant='standard'
          margin='normal'
          InputProps={{
            endAdornment: (
              <InputAdornment position="start">

                <SearchOutlinedIcon fontSize='large' />
             
              </InputAdornment>
            ),
          }}
          
          
          />

             {/* Button bar */}
            <Button variant='contained' onClick={e=>
            {
              e.preventDefault();
              list_data ? setList_data(false) : setList_data(true)
            }
            
            
            }> show data list</Button>
          </Grid>

        </Grid>
      

      <Grid item md={12} >
        
        <Grid
        container
        direction="row"
        justifyContent="center"
        alignItems="center"> 
        
        <Collapse in={list_data}>
          
          <Box
          padding={2}
          sx={{ 
            backgroundColor:'white',
            border:'2px solid #9B3C21',
            borderRadius: 2}}>
          
          
          <DataGrid
                  columns={columns}
                  getRowId={(rows)=> rows.Product_ID}
                  rows={filtered} // data na pinasok yung data 
                  
                  pageSize={10}
                  checkboxSelection={true} 

                  onSelectionModelChange={(ids) => {
                    setId(parseInt(ids))
                  }}
                />
    

          </Box>
        
        </Collapse> 

  
        
      </Grid>
    
    </Grid>

    <Box
          padding={2}
          sx={{ 
            backgroundColor:'white',
            }}>

            {/* <TextField fullWidth placeholder='Search product data' margin='normal'/>
            <TextField  fullWidth placeholder='Search product data' margin='normal'/>
            <TextField fullWidth placeholder='Search product data' margin='normal'/>
            <TextField fullWidth placeholder='Search product data' margin='normal'/>
            <TextField fullWidth placeholder='Search product data' margin='normal'/> */}


        </Box>
        
       

    









  </Grid>




    </div>
  )
}

export default Stocks
