import { React, useEffect, useState } from 'react';
import "./featuredInfo.css";
import ArrowUpwardIcon from '@mui/icons-material/ArrowUpward';
import { styled } from '@mui/material/styles';
import Box from '@mui/material/Box';
import Paper from '@mui/material/Paper';
import Grid from '@mui/material/Grid';
import axios from 'axios';
import WidgetLg from "./WidgetLg";
import WidgetTs from "./WidgetTs";


const Item = styled(Paper)(({ theme }) => ({
    backgroundColor: theme.palette.mode === 'dark' ? '#1A2027' : '#fff',
    ...theme.typography.body2,
    padding: theme.spacing(1),
    textAlign: 'center',
    color: theme.palette.text.secondary,
}));




export default function FeaturedInfo() {

    const [data, newData] = useState(null);

    useEffect(() => {
        fetch('http://localhost/Reactjs/cognate1%20api/totalorders.php')
            .then((response) => response.text())
        .then((response) => newData(response));
    }, []);

    const [data1, newData1] = useState(null);

    useEffect(() => {
        fetch('http://localhost/Reactjs/cognate1%20api/Todaysales.php')
            .then((response) => response.text())
            .then((response) => newData1(response));
    }, []);

    const [data2, newData2] = useState(null);

    useEffect(() => {
        fetch('http://localhost/Reactjs/cognate1%20api/Countpending.php')
            .then((response) => response.text())
            .then((response) => newData2(response));
    }, []);










    return <div>


        <Box sx={{ flexGrow: 1 }}
            margin={{ top: 0, right: 0, left: 0, bottom: 0 }}>
            <Grid container spacing={3}>

                <Grid item xs>

                    <div className="featured">
                        <div className="featuredItem">
                            <span className="featuredTitle">Sales Revenue</span>
                            <div className="featuredMoneyContainer">
                                <span className="featuredMoney">

                                    ₱{data}

                                </span>




                            </div>
                            <span className="featuredSub"></span>

                        </div>
                    </div>
                </Grid>
                <Grid item xs>

                    <div className="featuredItem">
                        <span className="featuredTitle">Daily Sales</span>
                        <div className="featuredMoneyContainer">
                            <span className="featuredMoney">

                                ₱{data1}



                            </span>

                        </div>

                    </div>

                </Grid>

                <Grid item xs={4}>
                    <div className="featuredItem">
                        <span className="featuredPending">Pending Orders</span>
                        <div className="featuredMoneyContainer">
                            <span className="featuredPendingMoney">

                                {data2}



                            </span>

                        </div>

                    </div>

                </Grid>









            </Grid>
        </Box>




    </div>;


















}
