import { 
  Fab, 
  Grid , 
  Typography, 
  Box, 
  Button, 
  FormControlLabel, 
  Alert, 
  Collapse, 
  Radio, 
  RadioGroup, 
  Select, 
  Chip, 
  MenuItem, 
  Checkbox, 
  ListItemText,
  TextField,
} from '@mui/material'
import Dialog, { DialogProps } from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import DialogTitle from '@mui/material/DialogTitle';
import React, { useEffect, useState } from 'react'
import { useNavigate } from 'react-router-dom';
import { CcTextField } from '../Stocks';
import AdapterDateFns from '@mui/lab/AdapterDateFns';
import LocalizationProvider from '@mui/lab/LocalizationProvider';
import MobileDatePicker from '@mui/lab/MobileDatePicker'
import moment from 'moment'
// ICONS
import ClearIcon from '@mui/icons-material/Clear';

import LOGO  from '../../icons/user.png'
import axios from 'axios';
import { DataGrid } from '@mui/x-data-grid';




const Add_data = () => {
// Routes
  let NavS = useNavigate();


// Date ITO
const [value,setValue] = useState(
  new Date()
)
  //JSON
  const [data, setData] = useState({
    "Customer_name": "",
    "Customer_email": "",
    "Customer_cell": "",
    "Customer_address": "",
    "Customer_purchase": "",
    "Customer_date": new Date(),
    "Customer_total": 0,
    "Customer_payment":  "Paid"
  })
// Show success
const [show_success,setShow_success] = useState()

// Show error
const [show_error,setShow_error] = useState(true)

// Show error show
const [show_err,setShow_err] = useState()

// Product purchase
const [selectname, setSelectname] = useState([])



// Menu props
const ITEM_HEIGHT = 48;
const ITEM_PADDING_TOP = 8;

const MenuProps = {
  PaperProps: {
    style: {
      maxHeight: ITEM_HEIGHT * 4.5 + ITEM_PADDING_TOP,
      width: 250,
    },
  },
};

// Message if data exist
const [Msg,setMsg] = useState()

// Add customer data
const add_customerdata = e => {

  e.preventDefault()  

  
  axios.post("http://localhost/REACTJS/cognate1%20api/Create_CustomerdataAPI.php", {
    "Customer_name": data.Customer_name,
    "Customer_email": data.Customer_email,
    "Customer_cell": data.Customer_cell,
    "Customer_address": data.Customer_address,
    "Customer_purchase": selectname.toString(),
    "Customer_date": moment(value,"mm-dd-yyyy").format().split('T')[0],
    "Customer_total": data.Customer_total,
    "Customer_payment":  data.Customer_payment
  })
  .then(res => 
    {
      setShow_success(res.data[0].Result)
      setShow_error(res.data[0].Result)
      setShow_err(res.data[0].Result)
      setMsg(res.data[0].Message)
    })
    setDisble(true)
  //  console.log(Object(selectname))
  }

// Data product
const [dataproduct,setDataproduct] = useState([])

// filtered fetch product name
const name = !dataproduct ? 
"empty" 
: dataproduct.map(name=>name.Product_name )

//Product name Proouct


// fetch name 
useEffect(() => {
 axios.get('http://localhost/REACTJS/cognate1%20api/ProductdataAPI.php')
 .then(res=> setDataproduct(res.data))



})



const columns = [

  // ID Column
  { field: 'Product_ID', 
    headerName: 'ID', 
    width: 70 ,
    headerAlign: 'center',

    renderCell: (cellValues) => {
      return (
        <div
          style={{
            color: "black",
            fontSize: 18,
            width: "100%",
            textAlign: "center"
          }}
        >
          {cellValues.value}
        </div>
      );
    }
  },

  // ID Column
  { field: 'Product_name', 
    headerName: 'name', 
    width: 200,
    headerClassName: 'super-app-theme--header',
    headerAlign: 'center', 

    renderCell: (cellValues) => {
      return (
        <div
          style={{
            color: "black",
            fontSize: 18,
            width: "100%",
            textAlign: "center"
          }}
        >
          {cellValues.value}
        </div>
      );
    }
  
  },


  { field: 'Product_price', 
    headerName: 'price', 
    width: 130,
    headerAlign: 'center', 

    renderCell: (cellValues) => {
      return (
        <div
          style={{
            color: "black",
            fontSize: 18,
            width: "100%",
            textAlign: "center"
          }}
        >
          {cellValues.value}
        </div>
      );
    }
  },
  
];

const [disble,setDisble] = useState(false)

  return ( <div>

  {/* Message Dialog */}
  
 
{/* // Whole Container */}
         {/* Alert Success */}
         <Collapse in={show_success}>
         <Alert severity="success"
         onClose={()=>{
           
           setShow_success(false) 
           setData({

            Customer_name: "",
            Customer_email: "",
            Customer_cell: "",
            Customer_address: "",
            Customer_purchase: [""],
            Customer_date: new Date(),
            Customer_total: 0,
            Customer_payment:  "Paid"


           })
           setSelectname([])
           setDisble(false)
         }}
         >
           Data has been updated
         </Alert>
       </Collapse>

{/* Alert Error */}
<Collapse fullWidth in={show_err ? false : true}>
         <Alert severity="error"
         onClose={()=>{
           
           setShow_err(true) 
           setShow_error(true)
           setDisble(false)
         }}
         >
           ATTENTION! textfield should not be empty!
         </Alert>
       </Collapse>

    <Grid container padding={6} paddingTop={2} >
    


    

{/* Container para sa button paar may padding */}
      <Grid container padding={2}>

               
        {/* Button cancel */}
        <Fab 
        color="primary"  
        aria-label="add" 
        onClick={e=>NavS("/Mainpage/Invoice")} 
        >
          <ClearIcon />
 
        </Fab>

      </Grid>

{/* Container for all components */}
      <Grid 
      container
      direction="row"
      justifyContent="center"
      alignItems="flex-start" 
      style={{
        backgroundColor:"white",
        border:"2px solid black",
        borderRadius: 5
      }}
      onClick={e=>
        {
          setData({...data,Customer_purchase: selectname.toString()})
        }
      
      
      }>

{/* CONTAINER FOR ICON LOGO */}
        <Grid item md={5} xs={12}>

          <Grid 
          container 
          padding={2}
          paddingTop={6}
          direction="row"
          justifyContent="center"
          alignItems="flex-start">

          {/* Picturebox */}
              <Box
              fixed
              component='img'
              marginY={2}

              sx={{
                width: 160,
                height: 160,
              }}
              src={LOGO} />

              <Grid md={12} padding={2}>
              {/* Name */}
              <Typography variant='h6'> Name:</Typography>

              <CcTextField 
              fullWidth 
              margin='normal'
              type='text'
              value={data.Customer_name}
              onChange={e=> setData({...data, Customer_name: e.target.value})}
              error={show_error? false : true}
              helperText={Msg ? Msg : null}
              onClick={()=>
                {
                  setShow_error(true)
                  setMsg(null)
                }
                
              }
              />

              {/* Email */}
              <Typography variant='h6'> Email:</Typography>

              <CcTextField  
              fullWidth 
              margin='normal'
              type='text'
              value={data.Customer_email}
              onChange={e=> setData({...data, Customer_email: e.target.value})}
              error={show_error? false : true}
              onClick={()=>setShow_error(true)}
              />

              {/* Cellphone number */}
              <Typography variant='h6'> Cellphone number:</Typography>
              
              <CcTextField  
              fullWidth 
              margin='normal'
              type='number'              
              value={data.Customer_cell}
              onChange={e=> setData({...data, Customer_cell: String(e.target.value)})}
              error={show_error? false : true}
              onClick={()=>setShow_error(true)}
              />

              
              {/* Address */}
              <Typography variant='h6'> Address:</Typography>
              <CcTextField  
              fullWidth 
              margin='normal' 
              multiline
              type='text'
              value={data.Customer_address}
              onChange={e=> setData({...data, Customer_address: String(e.target.value)})}
              error={show_error? false : true}
              onClick={()=>setShow_error(true)}
              
              /> 


            </Grid>

          </Grid>

          

        </Grid>


        {/* Components details */}
        <Grid container item md={7} xs={12} padding={2} paddingTop={13}>
          





{/* Purchase product */}
              <Typography variant='h6'> Purchase product:</Typography>
              
              {/* <Select
              multiple
              fullWidth
              value={selectname}
           
              onChange={(e)=>{

                setSelectname( e.target.value
                  // typeof e.target.value === 'string' ? e.target.value.split(',') 
                  // : e.target.value
                )


              }}
              renderValue={(selected) => (
              <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 0.5 }}>
                {selected.map((value) => (
                <Chip key={value} label={value} color='primary'/>
              ))}
              </Box>
              )}
              MenuProps={MenuProps}
              error={show_error? false : true}
              onClick={()=>setShow_error(true)}
              >

                {name.map(names=> (
                  <MenuItem
                  key={names}
                  value={names}
                  >
                    
                    <Checkbox checked={selectname.indexOf(names) >= 0} />
                    <ListItemText primary={names} />
                
                  </MenuItem>
                ))
     
                }

              </Select> */}

            <Select
              multiple
              fullWidth
              value={selectname}
           
              onChange={(e)=>{

                setSelectname( e.target.value
              //     // typeof e.target.value === 'string' ? e.target.value.split(',') 
              //     // : e.target.value
                )


              }}
              renderValue={(selected) => (
              <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 0.5 }}>
                {selected.map((value) => (
                <Chip key={value} label={value} color='primary'/>
              ))}
              </Box>
              )}
              MenuProps={MenuProps}
              error={show_error? false : true}
              onClick={()=>setShow_error(true)}
              >
                <Grid style={{
                  width:'100%',
                  height: 300
                }}>

                

                {/* <DataGrid
                columns={columns}
                getRowId={(rows)=> rows.Product_ID}
                rows={dataproduct} // data na pinasok yung data 
                checkboxSelection
                pageSize={10}
                 onCellClick={(param)=>
                {
                  setSelectname(param.row.Product_name)
                }}
                // onCellDoubleClick={edit_customerdata}
                /> */}
                </Grid>

              </Select>
            

            {/* <TextField fullWidth defaultValue={selectname} /> */}
            <Typography>{selectname}</Typography>
              <Grid style={{
                  width:'100%',
                  height: 300
                }}>

                

                <DataGrid
                columns={columns}
                getRowId={(rows)=> rows.Product_ID}
                rows={dataproduct} // data na pinasok yung data 
                checkboxSelection
                pageSize={10}
                 onCellClick={(param)=>
                {
                  setSelectname(param.row.Product_name)
                }}
                // onCellDoubleClick={edit_customerdata}
                />
                </Grid>
          

{/* Date */}   
              <Typography marginTop={2} variant='h6'> Date:</Typography>
              <Grid marginY={2} container>
                
                <LocalizationProvider dateAdapter={AdapterDateFns}>
                  <MobileDatePicker
                  error={show_error? false : true}
                  inputFormat="MM/dd/yyyy"
                  value={value}
                  onChange={(newValue) => setValue(newValue) }
                  renderInput={(params) => <CcTextField  {...params} 
                  onClick={()=>{
                    setShow_error(true)
                  }}
                  />}
          
                  />

                </LocalizationProvider>

    </Grid>
{/* Total product */}
              <Typography variant='h6'> Total:</Typography>
              
              <CcTextField  
              fullWidth 
              margin='normal'
              type='number'
              value={data.Customer_total}
              onChange={e=> setData({...data, Customer_total: String(e.target.value)})}
              error={show_error? false : true}
              onClick={()=>setShow_error(true)}
              />

    {/* Radio Button for payment status */}
        <RadioGroup
          row
          value={data.Customer_payment}
          onChange={e => {
            setData({...data,Customer_payment: e.target.value })
          }}
        
          >
          <FormControlLabel  value="Paid" control={<Radio />} label="Paid" />
          <FormControlLabel value="Pending" control={<Radio />} label="Pending" />
        </RadioGroup>

        </Grid>



        {/* Button cancel */}

        <Grid
        item
        container
        md={12}
        padding={5}
        direction="row"
        justifyContent="center"
        alignItems="flex-start">

        {/* Add item button */}
          <Button 
          variant='contained' 
          size='large' 
          style={{
            borderRadius: 50
          }}
          disabled={disble}
          onClick={add_customerdata}>
            Add item
          </Button>

        </Grid>

   

      </Grid>

    </Grid>
    </div>
)
}

export default Add_data
