import styled from '@emotion/styled'
import { AppBar, Button, DialogActions, DialogContent, Grid, TextField, Typography } from '@mui/material'
import { Box } from '@mui/system'
import React, { useState } from 'react'
import { useNavigate } from 'react-router-dom'
import axios from 'axios';

const Forgot_password = () => {
    let NavS = useNavigate();
// header for forgot password
    const Capp = styled(AppBar)({
        backgroundColor: "#9B3C21",
      
      })

// security verification value
const [sec,setSec]  = useState(
    {
        sec1: "",
        sec2: ""
    }
)

const onSubmit = e =>{
    console.log(sec)


      // HTTP REQUEST
  axios.post('http://localhost/REACTJS/cognate1%20api/LoginAPI.php', sec)
  .then(
    res=>{

      alert(res.data[0].Sec)

    } 
  )

}

  return (
    <div>

{/* pang break */}
        <br/>
        <br/>
        <br/>
        <br/>
        <br/>
        <br/>

        {/* Whole container */}
        <Grid
        container
        direction="column"
        justifyContent="center"
        alignItems="center"
        spacing={2}
        >

            {/* Item container */}
            <Grid item md={8} style={{ width: 700}}>
                
                {/* Header */}
                <Capp position='static'>
                    <Typography variant='h5' margin={2}>Forgot password</Typography>
                </Capp>

                {/* Components */}
                <Grid         
                container
                direction="column"
                justifyContent="center"
                alignItems="center"
                padding={2}
  
                style={
                    {
                        backgroundColor: "white",
                        border: "solid 2px black"
                    }
                }>
                    <form onSubmit={onSubmit()}>
                    {/* First Question */}
                    <Grid item>
                        <Typography variant='h6'> Unforgettable experience that you have already forgotten?</Typography>
                        <TextField 
                        multiline 
                        fullWidth 
                        placeholder='Answer this please' 
                        margin='normal'
                        helperText='I dont know'
                        value={sec.sec1}
                        onChange={
                            e=>{
                                setSec({...sec, sec1: e.target.value})
                            }
                        }
                        required/>
                    </Grid>

                    {/* Second Question */}
                    <Grid item >
                        <Typography variant='h6'> Something you neglected?</Typography>
                        <TextField 
                        required
                        multiline 
                        fullWidth 
                        placeholder='Answer this please' 
                        margin='normal'
                        helperText='I dont know'
                        value={sec.sec2}
                        onChange={
                            e=>setSec({...sec, sec2: e.target.value})
                        }
                        />
                    </Grid>

                                        {/* Second Question */}
                    <Grid item md={3} padding={1}>
                        <Button type='submit' fullWidth variant='contained'>
                            Submit
                        </Button>

                    </Grid>
                    <Grid item md={3} padding={1}>
                        <Button fullWidth variant='contained' onClick={e=>NavS("/")} >
                            Cancel
                        </Button>
                    </Grid>

                    </form>



                </Grid>
                
            </Grid>

        </Grid>
          
    </div>
  )
}

// {/* <Collapse in={true}>




// </Collapse> */}
export default Forgot_password
