// Library components

import { Grid, 
        InputAdornment,
        Typography,
        Fab,
        IconButton,
        Collapse,
        Button,
        FormControlLabel,
        Switch,
        Box,
} from '@mui/material'
import { CTextField } from '../products/Products' 
import { CcTextField } from '../Stocks';
import React, { useEffect, useState } from 'react'

// Icons
import SearchOutlinedIcon from '@mui/icons-material/SearchOutlined';
import AddIcon from '@mui/icons-material/Add';
import DeleteOutlineOutlinedIcon from '@mui/icons-material/DeleteOutlineOutlined';
import ClearIcon from '@mui/icons-material/Clear';

// Routes
import { useNavigate } from 'react-router-dom';
import { DataGrid } from '@mui/x-data-grid';
import axios from 'axios';

import LOGO  from '../../icons/user.png'



const Invoice = () => {
// Navigation
let NavS = useNavigate();


// Column header
const columns = [

  // ID Column
  { field: 'Customer_ID', 
    headerName: 'ID', 
    width: 70 ,
    headerAlign: 'center',

    renderCell: (cellValues) => {
      return (
        <div
          style={{
            color: "black",
            fontSize: 18,
            width: "100%",
            textAlign: "center"
          }}
        >
          {cellValues.value}
        </div>
      );
    }
  },

  // ID Column
  { field: 'Customer_name', 
    headerName: 'name', 
    width: 200,
    headerClassName: 'super-app-theme--header',
    headerAlign: 'center', 

    renderCell: (cellValues) => {
      return (
        <div
          style={{
            color: "black",
            fontSize: 18,
            width: "100%",
            textAlign: "center"
          }}
        >
          {cellValues.value}
        </div>
      );
    }
  
  },

  { field: 'Customer_address', 
    headerName: 'Address', 
    width: 130,
    headerAlign: 'center', 

    renderCell: (cellValues) => {
      return (
        <div
          style={{
            color: "black",
            fontSize: 18,
            width: "100%",
            textAlign: "center"
          }}
        >
          {cellValues.value}
        </div>
      );
    }

  
  },

  { field: 'Customer_email', 
    headerName: 'email', 
    width: 130,
    headerAlign: 'center', 

    renderCell: (cellValues) => {
      return (
      <div
      style={{
        color: "black",
        fontSize: 18,
        width: "100%",
        textAlign: "center"
        }}
      >
        {cellValues.value}
        </div>
        );
    }
  },

  { field: 'Customer_cell', 
    headerName: 'Cellphone number', 
    width: 150,
    headerAlign: 'center', 

    renderCell: (cellValues) => {
      return (
        <div
          style={{
            color: "black",
            fontSize: 18,
            width: "100%",
            textAlign: "center"
          }}
        >
          {cellValues.value}
        </div>
      );
    }
  },

  { field: 'Customer_purchase', 
  headerName: 'product purchase', 
  width: 150,
  headerAlign: 'center', 

  renderCell: (cellValues) => {
    return (
      <div
