//search data variable
const [search,setSearch] = useState("")

// Data 
const [Data,set] = useState()

// Edit true or not

const [edit,setEdit] = useState(false)

// Filtering data 
const filtered = !search ? 
Data 
: Data.filter((person) => 
    person.Customer_name.toLowerCase().includes(search.toLowerCase())
  );

 // Status
 const [status,setStatus] = useState({
  "status" : "paid",
   "check" : true
})


  //JSON
const [data, setData] = useState({
    "Customer_name": "",
    "Customer_email": "",
    "Customer_cell": "+63",
    "Customer_address": "",
    "Customer_purchase": "",
    "Customer_date": "",
    "Customer_total": 0,
    "Customer_payment": ""
  })

// Htttp get request
useEffect(() => {
  axios.get('http://localhost/REACTJS/cognate1%20api/CustomerdataAPI.php')
.then(res=>set(res.data))
.catch(err=>console.log(err)) 
})

  // Delete customerdata
  const delete_customerdata = (param) => {
  
    if(param.field === "action")
    {
      axios.post("http://localhost/REACTJS/cognate1%20api/Delete_CustomerdataAPI.php", { "Customer_ID": param.id } )
      .then(res => console.log(res.data))
    }
  
}

  return (
    <div>
      <Collapse in={edit}>
      {/* Whole Container */}
        <Grid container spacing={2}>
 
        
{/* Search bar container */}
          <Grid item md={9} xs={12}>

          

            {/* Para may padding */}
           <Grid 
           container 
           padding={5}
           direction="row"
           justifyContent="center"
           alignItems="flex-start">

             {/* Search bar */}
              <CTextField 
              variant='standard' 
              fullWidth 
              placeholder='Searchbar'
              value={search}
              onChange={e=>setSearch(e.target.value)}

              InputProps={{
                endAdornment: (
                  <InputAdornment position="start">
    
                    <SearchOutlinedIcon fontSize='large' />
                 
                  </InputAdornment>
                ),
              }}
              />
           </Grid>
    
          </Grid>

          {/* Conatainer button */}
          <Grid item md={3} xs={12}>

            {/* Container para may padding */}
            <Grid 
            container 
            padding={5}           
            direction="row"
            justifyContent="center"
            alignItems="flex-start">

              {/* Button add */}
              <Fab 
              color="primary" 
              variant='extended' 
              aria-label="add" 
              onClick={e=>NavS("/Mainpage/Invoice/AddData")} 
              >
                <AddIcon />
                add data
              </Fab>

            </Grid>
            
          </Grid>



          {/* Container for table */}
          <Grid item md={12}>
            
            {/* Para may padding */}
            <Grid 
            container             
            direction="row"
            justifyContent="center"
            alignItems="flex-start"
            padding={5}>

              {/* pang size at background color */}
              <Grid 
              container
              style={{
                height:550 ,
                width:'100%',
                backgroundColor: 'White',
                border:'2px solid black',
                borderRadius: 3               
              }}
              >
                <DataGrid
                columns={columns}
                getRowId={(rows)=> rows.Customer_ID}
                rows={filtered} // data na pinasok yung data 
                  
                pageSize={10}
                onCellClick={delete_customerdata}
                />

              </Grid>
           
          
            </Grid>

          </Grid>

 
        </Grid>
        
      </Collapse>
        
{/* Edit profile */}
          <Collapse in={edit ? false : true}>

          {/* // Whole Container */}
    <Grid container padding={6} paddingTop={2} >

{/* Container para sa button paar may padding */}
      <Grid container padding={2}>
                      
        {/* Button cancel */}
        <Fab 
        color="primary"  
        aria-label="add" 
      onClick={e=>setEdit(true)} 
        >
          <ClearIcon />
 
        </Fab>
        <Typography padding={1} paddingLeft={2} variant='h4'>
        Edit data profile
      </Typography>

      </Grid>


{/* Container for all components */}
      <Grid 
      container
      direction="row"
      justifyContent="center"
      alignItems="flex-start" 
      style={{
        backgroundColor:"white",
        border:"2px solid black",
        borderRadius: 5
      }}>

{/* CONTAINER FOR ICON LOGO */}
        <Grid item md={5} xs={12}>

          <Grid 
          container 
          padding={2}
          paddingTop={6}
          direction="row"
          justifyContent="center"
          alignItems="flex-start">

          {/* Picturebox */}
              <Box
              fixed
              component='img'
              marginY={2}

              sx={{
                width: 160,
                height: 160,
              }}
              src={LOGO} />

              <Grid md={12} padding={2}>
              {/* Name */}
              <Typography variant='h6'> Name:</Typography>

              <CcTextField 
              fullWidth 
              margin='normal'
              type='text'
              value={data.Customer_name}
              onChange={e=> setData({...data, Customer_name: e.target.value})}
              
              />

              {/* Email */}
              <Typography variant='h6'> Email:</Typography>

              <CcTextField  
              fullWidth 
              margin='normal'
              type='text'
              value={data.Customer_email}
              onChange={e=> setData({...data, Customer_email: e.target.value})}
              />

              {/* Cellphone number */}
              <Typography variant='h6'> Cellphone number:</Typography>
              
              <CcTextField  
              fullWidth 
              margin='normal'
              type='number'              
              value={data.Customer_cell}
              onChange={e=> setData({...data, Customer_cell: String(e.target.value)})}
              />

              
              {/* Address */}
              <Typography variant='h6'> Address:</Typography>
              <CcTextField  
              fullWidth 
              margin='normal' 
              multiline
              type='text'
              value={data.Customer_address}
              onChange={e=> setData({...data, Customer_address: String(e.target.value)})}
              
              /> 


            </Grid>

          </Grid>

          

        </Grid>


        {/* Components details */}
        <Grid container item md={7} xs={12} padding={2} paddingTop={13}>
          
{/* Purchase product */}
              <Typography variant='h6'> Purchase product:</Typography>
              
              <CcTextField  
              fullWidth 
              multiline 
              margin='normal' 
              placeholder='please input the item ID here'
              type='text'
              value={data.Customer_purchase}
              onChange={e=> setData({...data, Customer_purchase: String(e.target.value)})}
              
              />

{/* Date */}
              <Typography variant='h6'> Date:</Typography>
              
              <CcTextField  
              fullWidth 
              margin='normal'
              type='text'
              value={data.Customer_date}
              onChange={e=> setData({...data, Customer_date: String(e.target.value)})}
              
              />

{/* Total product */}
              <Typography variant='h6'> Total:</Typography>
              
              <CcTextField  
              fullWidth 
              margin='normal'
              type='number'
              value={data.Customer_total}
              onChange={e=> setData({...data, Customer_total: String(e.target.value)})}
              
              />

{/* Toggle Button for payment status */}
              <FormControlLabel  
              label= {status.check ? "Paid" : "Pending"} 
              control={
              <Switch 
              checked={status.check} 
              onChange={()=> 
                {
                  status.check ? 
                  setData({...data,Customer_payment: "Paid"})
                  : setData({...data,Customer_payment: "Pending"})
                  setStatus({...status, check: !status.check})
                }
              }/>
              } />
            

        </Grid>



        {/* Button cancel */}

        <Grid
        item
        container
        md={12}
        padding={5}
        direction="row"
        justifyContent="center"
        alignItems="flex-start">

        {/* Add item button */}
          <Button variant='contained' size='large' style={{
            borderRadius: 50
          }}>
            Update data
          </Button>

        </Grid>

   

      </Grid>

    </Grid>
  </Collapse>

    </div>
  )
}

