import {  
  Typography, 
  IconButton, 
  Toolbar, 
  Drawer, 
  List, 
  ListItemText, 
  ListItemIcon, 
  ListItemButton, 
  Divider,
  Grid,
  Box,
  ButtonGroup,
  Button
} from '@mui/material';

import React, { useState } from 'react'
import { 
  styled, 
  useTheme 
} from '@mui/material/styles';

import MuiAppBar from '@mui/material/AppBar';

///ICONS
import MenuRoundedIcon from '@mui/icons-material/MenuRounded';
import ArrowBackRoundedIcon from '@mui/icons-material/ArrowBackRounded';
import LOGO  from './Logo.ico'

const drawerWidth = 240;
///Butoons

// Cutom Button
const CButton = styled(Button)({
  boxShadow: "none",
  textTransform: "none",
  fontSize: 18,
  padding: "6px 12px",
  lineHeight: 2.5,

});




const creatse = (e) =>{

      <h1 style={{
        textAlign: center
 



      }}>HELLO FRIERND</h1>


}


// CUSTOM BUTTON
const buttons = [
  <CButton onClick={creatse}>One</CButton>,
  <CButton onClick={creatse}>Two</CButton>,
  <CButton Click={creatse} >Three</CButton>,
];






// Custom Header
const AppBar = styled(MuiAppBar, {
  shouldForwardProp: (prop) => prop !== 'open',
})(({ theme, open }) => ({
  transition: theme.transitions.create(['margin', 'width'], {
    easing: theme.transitions.easing.sharp,
    duration: theme.transitions.duration.leavingScreen,
  }),
  ...(open && {
    width: `calc(100% - ${drawerWidth}px)`,
    marginLeft: `${drawerWidth}px`,
    transition: theme.transitions.create(['margin', 'width'], {
      easing: theme.transitions.easing.easeOut,
      duration: theme.transitions.duration.enteringScreen,
    }),
  }),
}));


// function

const Mainpage = () => {
  const [open ,setOpen] = useState(true)
  // const theme = useTheme();

  return (

// HEADER
  <AppBar position='fixed' open={open} 
  onClick={() => {
    if(open){
      setOpen(false)
    }
  }}>
    

      <Toolbar>

{/*Button for side bar  */}
      <IconButton
            size="large"
            edge="start"
            color="inherit"
            aria-label="menu"
            sx={{ mr: 2, ...(open && { display: 'none' }) }}
           onClick={(e) => {
             e.preventDefault()
             setOpen(true)
           }}>
{/* ICON */}
            <MenuRoundedIcon fontSize='large' />

          </IconButton>
{/* TEXT */}

          <Typography variant="h6" noWrap>
            We are the love sick girl
          </Typography>

{/* SIDE BAR */}
      <Drawer
// permanent
      variant="persistent"
      anchor="left"
      open={open}
      sx={{
        
        width: drawerWidth,
        flexShrink: 0,
        '& .MuiDrawer-paper': {
          width: drawerWidth,
          boxSizing: 'border-box',
        },
      }}>
        
        <Grid           
          item 
  
          container
          direction="column"
          justifyContent="center"
          alignItems="center">
            
            <Box
            fixed
            component='img'
            marginY={8}

            sx={{
              width: 200,
              height: 100,
            }}
            src={LOGO} />
        
        </Grid>

        <Divider variant="middle" />

      <List disablePadding>



      <ButtonGroup
        fullWidth
        orientation="vertical"
        aria-label="vertical contained button group"
        variant="text"
        margin='dense'
      >
        {buttons}
      </ButtonGroup>

      </List>
  
      </Drawer>
    </Toolbar>
  </AppBar>




   
  )
}

export default Mainpage
