// Library components

import { Grid, 
        InputAdornment,
        Typography,
        Fab,
        IconButton,
        Collapse,
        Button,
        FormControlLabel,
        Switch,
        Box,
        Alert,
        RadioGroup,
        Radio,
        Select,
        Chip,
        MenuItem,
        Checkbox,
        ListItemText,
} from '@mui/material'
import { CTextField } from '../products/Products' 
import { CcTextField } from '../Stocks';
import React, { useEffect, useState } from 'react'
import AdapterDateFns from '@mui/lab/AdapterDateFns';

// Icons
import SearchOutlinedIcon from '@mui/icons-material/SearchOutlined';
import AddIcon from '@mui/icons-material/Add';
import DeleteOutlineOutlinedIcon from '@mui/icons-material/DeleteOutlineOutlined';
import ClearIcon from '@mui/icons-material/Clear';
import moment from 'moment'

// Routes
import { useNavigate } from 'react-router-dom';
import { DataGrid } from '@mui/x-data-grid';
import axios from 'axios';


import LOGO  from '../../icons/user.png'
import { LocalizationProvider, MobileDatePicker } from '@mui/lab';


// Transfer data to edit data
export var dataT = {}




const Invoice = () => {
// Navigation
let NavS = useNavigate();


// Column header
const columns = [

  // ID Column
  { field: 'Customer_ID', 
    headerName: 'ID', 
    width: 70 ,
    headerAlign: 'center',

    renderCell: (cellValues) => {
      return (
        <div
          style={{
            color: "black",
            fontSize: 18,
            width: "100%",
            textAlign: "center"
          }}
        >
          {cellValues.value}
        </div>
      );
    }
  },

  // name Column
  { field: 'Customer_name', 
    headerName: 'name', 
    width: 200,
    headerClassName: 'super-app-theme--header',
    headerAlign: 'center', 

    renderCell: (cellValues) => {
      return (
        <div
          style={{
            color: "black",
            fontSize: 18,
            width: "100%",
            textAlign: "center"
          }}
        >
          {cellValues.value}
        </div>
      );
    }
  
  },



  { field: 'Customer_email', 
    headerName: 'email', 
    width: 130,
    headerAlign: 'center', 

    renderCell: (cellValues) => {
      return (
      <div
      style={{
        color: "black",
        fontSize: 18,
        width: "100%",
        textAlign: "center"
        }}
      >
        {cellValues.value}
        </div>
        );
    }
  },

  { field: 'Customer_cell', 
    headerName: 'Cellphone number', 
    width: 150,
    headerAlign: 'center', 

    renderCell: (cellValues) => {
      return (
        <div
          style={{
            color: "black",
            fontSize: 18,
            width: "100%",
            textAlign: "center"
          }}
        >
          {cellValues.value}
        </div>
      );
    }
  },


{ field: 'Customer_date', 
headerName: 'date', 
width: 150,
headerAlign: 'center', 

renderCell: (cellValues) => {
  return (
    <div
      style={{
        color: "black",
        fontSize: 18,
        width: "100%",
        textAlign: "center"
      }}
    >
      {cellValues.value}
    </div>
  );
}
},

{ field: 'Customer_total', 
headerName: 'total payment', 
width: 130,
headerAlign: 'center', 

renderCell: (cellValues) => {
  return (
    <div
      style={{
        color: "black",
        fontSize: 18,
        width: "100%",
        textAlign: "center"
      }}
    >
      {cellValues.value}
    </div>
  );
}
},

{ field: 'Customer_payment', 
headerName: 'status', 
width: 130,
headerAlign: 'center', 

renderCell: (cellValues) => {
  return (
    <div
      style={{
        color: "black",
        fontSize: 18,
        width: "100%",
        textAlign: "center"
      }}
    >
      {cellValues.value}
    </div>
  );
}
},
  
// delete
  {
    field: 'action',
    headerName: 'Delete',
    width: 130,
    headerAlign: 'center', 
    renderCell: () => {
      return (
        <div
          style={{
            width: "100%",
            textAlign: "center",
            // marginRight: '2'
          }}
        >
         

        <IconButton color="primary" aria-label="delete">
            <DeleteOutlineOutlinedIcon/>
        </IconButton>
         
        </div>


      );
    }
  },
  // edit
  {
    field: 'View',
    headerName: 'View',
    width: 130,
    headerAlign: 'center', 
    renderCell: () => {
      return (
        <div
          style={{
            width: "100%",
            textAlign: "center",
            // marginRight: '2'
          }}
        >
         

        <IconButton color="primary" aria-label="delete">
            <DeleteOutlineOutlinedIcon/>
        </IconButton>
         
        </div>


      );
    }
  }

];
//search data variable
const [search,setSearch] = useState("")

// Data 
const [Data,set] = useState()

