import axios from 'axios';
import React, { useEffect, useState } from 'react'

// icons

import AddIcon from '@mui/icons-material/Add';
import DeleteOutlineOutlinedIcon from '@mui/icons-material/DeleteOutlineOutlined';
import SearchOutlinedIcon from '@mui/icons-material/SearchOutlined';
import ArchiveOutlinedIcon from '@mui/icons-material/ArchiveOutlined';
import SpeedDialIcon from '@mui/material/SpeedDialIcon';
import { Fab, Grid, IconButton, Tooltip, Typography } from '@mui/material';
import { useNavigate } from 'react-router-dom';



const Show_archive = () => {
// Column header

const columns = [

    // ID Column
    { field: 'Product_ID', 
      headerName: 'ID', 
      width: 70 ,
      headerAlign: 'center',

      renderCell: (cellValues) => {
        return (
          <div
            style={{
              color: "black",
              fontSize: 18,
              width: "100%",
              textAlign: "center"
            }}
          >
            {cellValues.value}
          </div>
        );
      }
    },

    // ID Column
    { field: 'Product_name', 
      headerName: 'name', 
      width: 200,
      headerClassName: 'super-app-theme--header',
      headerAlign: 'center', 

      renderCell: (cellValues) => {
        return (
          <div
            style={{
              color: "black",
              fontSize: 18,
              width: "100%",
              textAlign: "center"
            }}
          >
            {cellValues.value}
          </div>
        );
      }
    
    },

    { field: 'Product_category', 
      headerName: 'category', 
      width: 130,
      headerAlign: 'center', 

      renderCell: (cellValues) => {
        return (
          <div
            style={{
              color: "black",
              fontSize: 18,
              width: "100%",
              textAlign: "center"
            }}
          >
            {cellValues.value}
          </div>
        );
      }
  
    
    },

    { field: 'Product_stocks', 
      headerName: 'stocks ', 
      width: 130,
      headerAlign: 'center', 

      renderCell: (cellValues) => {
        return (
        <div
        style={{
          color: "black",
          fontSize: 18,
          width: "100%",
          textAlign: "center"
          }}
        >
          {cellValues.value}
          </div>
          );
      }
    },

    { field: 'Product_price', 
      headerName: 'price', 
      width: 130,
      headerAlign: 'center', 

      renderCell: (cellValues) => {
        return (
          <div
            style={{
              color: "black",
              fontSize: 18,
              width: "100%",
              textAlign: "center"
            }}
          >
            {cellValues.value}
          </div>
        );
      }
    },
    
// delete
    {
      field: 'action',
      headerName: 'Action',
      width: 130,
      headerAlign: 'center', 
      renderCell: () => {
        return (
          <div
            style={{
              width: "100%",
              textAlign: "center",
              // marginRight: '2'
            }}
          >
           

          <IconButton color="primary" aria-label="delete">
              <DeleteOutlineOutlinedIcon/>
          </IconButton>
           
  
          </div>


        );
      }
    }

  ];



  // archive data
  const [archive_Data, setArchive_data] = useState()

  // show archive

  useEffect(() => {

    axios.get("http://localhost/REACTJS/cognate1%20api/Archive_productdataAPI.php")
    .then(res => {
      setArchive_data(res.data)
    })

  });

  let NavS = useNavigate();

  return (
    <div>
     

          <Grid
        container
        direction="row"
        justifyContent="flex-start"
        alignItems="center"
        padding={2}>


{/*button add item */}
<Grid item>


            <Tooltip title="Add Item">
              <Fab color="primary" aria-label="add" onClick={e=>NavS("/Mainpage/Products/AddItem")} >
                <SpeedDialIcon />
              </Fab>
            </Tooltip>  
    
            </Grid>
     
            


        </Grid>
    </div>
  )
}

export default Show_archive
