import { Autocomplete, Box, Button,  Grid, TextField, Typography } from '@mui/material'
import { styled } from "@mui/material/styles";s
import React, { useState } from 'react'




// Custom TextField

const CTextField = styled(TextField)({
  '& .MuiInputBase-input': {

    position: 'relative',
    fontSize: 20,
    padding: '12px',

  },
  "& label.Mui-focused": {
    color: "#9B3C21"
  },

  "& .MuiOutlinedInput-root": {
    "& fieldset": {
      border: "2px solid #000000",
    },
    "&.Mui-focused fieldset": {

      border: "2px solid #9B3C21"
    }
  }
});


const Add_item = () => {

  const top100Films = [
  //   'The Shawshank Redemption',
  // 'The Godfather','The Godfather: Part II','The Dark Knight',
  // '12 Angry Men',"Schindler's List",'Pulp Fiction'];



    { label: 'The Shawshank Redemption', year: 1994 },
    { label: 'The Godfather', year: 1972 },
    { label: 'The Godfather: Part II', year: 1974 },
    { label: 'The Dark Knight', year: 2008 },
    { label: '12 Angry Men', year: 1957 },
    { label: "Schindler's List", year: 1993 },
    { label: 'Pulp Fiction', year: 1994 }];

const [category,setCategory] = useState([])


  return (
    <div>


      {/* WHOLE CONTAINER LOADS */}
      <Grid container spacing={2} style={{
        border:'2px solid red'
      }}>


        {/* Add Item */}
        <Grid containe marginTop={10} marginLeft={10}>
          <Typography variant='h3'> Add new item</Typography>
        </Grid>

        {/* container inputs */}
        <Grid
        fixed
          container
          direction="row"
          justifyContent="center"
          alignItems="center">

{/* isa pang container para maayos ang margin */}
          <Grid
          container
          fixed
          marginY={2}
          marginLeft={5}
          marginRight={2}
          direction="row"
          justifyContent="center"
          alignItems="center"
    
          >
      
            <Grid container
            margin={2}
                 direction="row"
                 justifyContent="center"
                 alignItems="center"
                 style={{
                  width:800,
                  border: '2px solid black',
                  borderRadius: '8px',
                  backgroundColor: 'white'
                 }}
                 >

          {/* Label Productname */}
              <Grid container  item md={5} justifyContent="center" margin={2}>
                 <Typography  variant='h5'>Product name</Typography>
              </Grid>

          {/* Input Productname*/}
              <Grid item md={6} marginX={1}>
                <CTextField fullWidth type='text' placeholder='Product name' variant="outlined" margin="normal"/>
              </Grid>


{/* ////////////////////////////////////////////////////////// */}

  {/* Label Productname */}
              <Grid container item md={5} justifyContent="center" margin={2}>
                 <Typography  variant='h5'>Product Category</Typography>
              </Grid>

{/* Label Category */}
              <Grid item md={6} marginX={1}>


                <Autocomplete
                freeSolo
                disablePortal
                options={top100Films.map((option) => option.label)}
                onChange={(event, value) => setCategory(value)}
                renderInput={
                  (params) =>

                  // Textfield
                  <CTextField
                  {...params}
                  fullWidth 
                  value={category}
                  onChange={e=>setCategory(e.target.value)}
                  type='text' 
                  placeholder='Product category'
                  variant="outlined" 
                  margin="normal"/>} />       

                <Typography variant='h5'>{category}</Typography>       
         
        
              </Grid>

{/* ////////////////////////////////////////////////////////// */}

              <Grid container item md={5} justifyContent="center" margin={2}>
                 <Typography  variant='h5'>Product name</Typography>
              </Grid>
              <Grid item md={6} marginX={1}>
                <CTextField fullWidth  placeholder='Product name' variant="outlined" margin="normal"/>
              </Grid>
              <Grid container item md={5} justifyContent="center" margin={2}>
                 <Typography  variant='h5'>Product name</Typography>
              </Grid>
              <Grid item md={6} marginX={1}>
                <CTextField fullWidth  placeholder='Product name' variant="outlined" margin="normal"/>
              </Grid>
              
            </Grid>
            


          </Grid>

          <Button 
          variant='contained'  
          style={{
            borderRadius: '10px',
            width:200,
            marginBottom:10,
            padding:10
          }}> create </Button>

            <Button variant='contained'  style={{
            borderRadius: '10px',
            width:200,
            marginBottom:10,
            padding:10
          }}> cancel </Button>

        </Grid>

        













      </Grid>
      
    </div>
  )
}

export default Add_item
