import { Grid, Box,Paper, Typography, Stack, TextField, InputAdornment, Button, IconButton, Fab, Divider, Collapse, Alert, fabClasses, Fade } from '@mui/material'
import { styled } from "@mui/material/styles";

import React, { useEffect, useRef, useState } from 'react'

//ICONS
import CloseIcon from '@mui/icons-material/Close';
import BorderColorOutlinedIcon from '@mui/icons-material/BorderColorOutlined';

import LOGO  from '../icons/user.png'
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { ReviewsSharp } from '@mui/icons-material';

// Custom TextField

const CTextField = styled(TextField)({
    '& .MuiInputBase-input': {
  
      position: 'relative',
      fontSize: 20,
      padding: '12px',
  
    },
    "& .MuiOutlinedInput-root.Mui-disabled": {
      "&  fieldset": {
        
        borderColor: "#FFFFFF",
        
      },
      "& text": {
        color: "#000000"
      }

    },
    "& label.Mui-focused": {
      color: "#9B3C21"
    },

    "& .MuiOutlinedInput-root": {
      "& fieldset": {
        border: "2px solid #000000",
      },
      "&.Mui-focused fieldset": {
  
        border: "2px solid #9B3C21"
      }
    }
  });

const Edit_profile = () => {


//Login_Data storage
  const [edit_data, setEdit_data] = useState({
    "email" : "",
    "password" : ""
  })

//enable hooks
  const [enble, setEnble] = useState(true)

//For alert
  const [show_alert,setShow_alert] = useState()

//Reference

const refs = useRef('')

const ref = () => {
      refs.current = refs.current.value + "ako@gmail.com"
     // refs.current.value = edit_data.password
      
}

//json conatainer data 
const [edi, setEdi]= useState({
  "email" : "",
  "password" : ""
})




// NAVIGATION TO ANOTHER PAGE
    let nv = useNavigate()

    useEffect(() => {

      ref()
      
      // axios.get('http://localhost/REACTJS/cognate1%20api/View_LoginAPI.php')
      // .then(res=> 
      //   {
      //     setEdit_data({...edit_data, email : res.data[0].email})
      //   }
      //   )
      //   .catch(err=>console.log(err))

        
   
    });
  return (
    <div>
      <Collapse in={show_alert}>
        <Alert severity="success"
           onClose={()=>
            {
              setShow_alert(false)
              setEnble(true)
            }

           
           }>
           Data has been updated
        </Alert>
      </Collapse>

        <Grid
        container
        direction="column "
        justifyContent="center"
        alignItems="center">

        <Box padding={2} paddingTop={10}>

            <Grid 
            component={Paper} 
            variant='outlined' 
            padding={3}
            style={{
                backgroundColor:'white',
                border:'2px solid black'
            }}>
                <Stack
                direction="column"
                justifyContent="center"
                alignItems="center"
                spacing={2}>

                  {/* Logo */}
                    <Box 
                    fixed
                    component='img'
                    style={{
                        width:120,
                        height:120,
                    }}
                    src={LOGO}/>

                    {/* Label Name */}
                    <Typography  variant='h4'>Francis Maneclang</Typography>
                    <Typography  variant='h6'>CEO</Typography>

{/* Edit and cancel edit button */}
                    <Grid container justifyContent="flex-end">

                      <IconButton color='primary' onClick={e=>{
                        e.preventDefault(); 
                        enble ?
                        setEnble(false): setEnble(true)}}>
                          
                          {
                            enble ? <BorderColorOutlinedIcon fontSize='large' /> : <CloseIcon fontSize='large' />
                          }
                          
                        
                      </IconButton>

                    </Grid>
                   

{/* email edit */}


         
                    <CTextField 
                    fullWidth 
                    type='email'  
                    variant="outlined" 
                    margin="normal"
                        
                    placeholder='Enter your new email'
                   inputRef={refs}
                    
                    disabled={enble}

                    defaultValue = {edi.email}
                    onChange = {e => setEdi({...edi, email: e.target.value})}
                    />

      
                  

{/* password edit */}
                    <CTextField 
                    fullWidth 
                    type='password'
    
                    placeholder='Enter your new password'
                    variant="outlined" 
                    margin="normal"

                    disabled={enble}
                    
                   // inputRef={refs}
                    // defaultValue = {}
                    // onChange = {e => setEdit_data({...edit_data, password: e.target.value})}
                    />

                    {enble === true && <Divider>Click edit button to edit</Divider> }              
                  
                  {/* // <Fade in={true}> */}
                     <CTextField 
                      fullWidth 
                      type='password'
    
                      placeholder='Enter your new password'
                      variant="outlined" 
                      margin="normal"

                  
                    
                      />

                      <CTextField 
                      fullWidth 
                      type='password'
    
                      placeholder='Enter your new password'
                      variant="outlined" 
                      margin="normal"

                     
                      />
                     {/* // </Fade> */}
                     
                   
                    <Button
                    variant='contained'

                    disabled={enble}  

                    style={{
                    borderRadius: '20px',
                    width:150,
                    padding:10,
                    }}
                    onClick={()=>setShow_alert(true)}
                    
                    
                    > Save </Button>

                    <Button 
                    variant='contained' 
                    style={{
                    borderRadius: '20px',
                    width:150,
                    padding:10}}
                    
                    onClick={e=> nv("/Mainpage")}
                    
                    > cancel </Button>

                    

      


                </Stack>
              




              


            




            </Grid>
       
                

            


         
    
        </Box>
         
        </Grid>
    </div>
  )
}

export default Edit_profile
