import { AppBar, Typography, IconButton, Toolbar, Drawer, makeStyles} from '@mui/material';
import React, { useState } from 'react'
import MenuRoundedIcon from '@mui/icons-material/MenuRounded';


const drawerWidth = 240;

// CSS
const useStyles = makeStyles((theme) => ({
  root: {
    display: 'flex',
  },
  appBar: {
    transition: theme.transitions.create(['margin', 'width'], {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
  },
  appBarShift: {
    width: `calc(100% - ${drawerWidth}px)`,
    marginLeft: drawerWidth,
    transition: theme.transitions.create(['margin', 'width'], {
      easing: theme.transitions.easing.easeOut,
      duration: theme.transitions.duration.enteringScreen,
    }),
  },
  menuButton: {
    marginRight: theme.spacing(2),
  },
  hide: {
    display: 'none',
  },
  drawer: {
    width: drawerWidth,
    flexShrink: 0,
  },
  drawerPaper: {
    width: drawerWidth,
  },
  drawerHeader: {
    display: 'flex',
    alignItems: 'center',
    padding: theme.spacing(0, 1),
    // necessary for content to be below app bar
    ...theme.mixins.toolbar,
    justifyContent: 'flex-end',
  },
  content: {
    flexGrow: 1,
    padding: theme.spacing(3),
    transition: theme.transitions.create('margin', {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
    marginLeft: -drawerWidth,
  },
  contentShift: {
    transition: theme.transitions.create('margin', {
      easing: theme.transitions.easing.easeOut,
      duration: theme.transitions.duration.enteringScreen,
    }),
    marginLeft: 0,
  },
}));

export default function Sidebar() {
  const [opem ,setOpem] = useState(false)

  return (
// HEADER
  <AppBar position='fixed' className={(useStyles.AppBar,{[useStyles.appBarShift]: open ,})}> 

      <Toolbar>

{/*Button for side bar  */}
      <IconButton
            size="large"
            edge="start"
            color="inherit"
            aria-label="menu"
            sx={{ mr: 2 }}
           onClick={(e) => {
             e.preventDefault()
             setOpem(true)
           }}>
{/* ICON */}
            <MenuRoundedIcon />

          </IconButton>
{/* TEXT */}

          <Typography variant="h6" noWrap>
            We are the love sick girl
          </Typography>

          {/* SIDE BAR */}
      <Drawer
      variant="persistent"
      anchor="left"
      open={opem}>
        
        <IconButton
        size="large"
        edge="start"
        color="inherit"
        aria-label="menu"
        // sx={{ mr: 3 }}
        onClick={
          (e) => {
            e.preventDefault()
            setOpem(false)
            }
          }>
            <MenuRoundedIcon />
            
        </IconButton>
      
      </Drawer>

    </Toolbar>

  </AppBar>




   
  )
}
