import { Button, Collapse, Divider, Grid, IconButton, InputAdornment, Stack, TextField, Toolbar, Typography } from '@mui/material'
import { Box } from '@mui/system'
import { DataGrid } from '@mui/x-data-grid'
import React, { useEffect, useState } from 'react'
import { CTextField } from './products/Products'

import SearchOutlinedIcon from '@mui/icons-material/SearchOutlined';
import ModeEditOutlinedIcon from '@mui/icons-material/ModeEditOutlined';

import axios from 'axios'
import styled from '@emotion/styled'





// Custom TextField
const CcTextField = styled(TextField)({
  '& .MuiInputBase-input': {

    position: 'relative',
    fontSize: 20,
    padding: '12px',

  },
  "label":{
    color: "#000000"

  },
  "& .MuiOutlinedInput-root.Mui-disabled": {
    "&  fieldset": {
      
      borderColor: "#FFFFFF",
      
    },
    "& text": {
      color: "#000000"
    }

  },
  "& label.Mui-focused": {
    color: "#9B3C21"
  },

  "& .MuiOutlinedInput-root": {
    "& fieldset": {
      border: "2px solid #000000",
    },
    "&.Mui-focused fieldset": {

      border: "2px solid #9B3C21"
    }
  }
});


//MAIN FUNCTION
const Stocks = () => {

///Product ID
const [id,setId] = useState()

//Consta for table
const [list_data, setList_data] = useState(true)

// Columns
const columns = [

  // ID Column
  { field: 'Product_ID', 
    headerName: 'ID', 
    width: 70 ,
    headerAlign: 'center',

    renderCell: (cellValues) => {
      return (
        <div
          style={{
            color: "black",
            fontSize: 18,
            width: "100%",
            textAlign: "center"
          }}
        >
          {cellValues.value}
        </div>
      );
    }
  },

  // Product Name
  { field: 'Product_name', 
    headerName: 'Product_name', 
    width: 300,
    headerClassName: 'super-app-theme--header',
    headerAlign: 'center', 

    renderCell: (cellValues) => {
      return (
        <div
        style={{
            color: "black",
            fontSize: 18,
            width: "100%",
            textAlign: "center"
          }}
        >
          {cellValues.value}
        </div>
      );
    }
  
  },
    // Edit
    {
      field: 'action',
      headerName: 'Action',
      width: 125,
      headerAlign: 'center', 
      renderCell: () => {
        return (
          <div
            style={{
              width: "100%",
              textAlign: "center",
              // marginRight: '2'
            }}
          >
           
          <IconButton color="primary" aria-label="edit" onClick={e=>setList_data(false)}>
              <ModeEditOutlinedIcon/>
          </IconButton>
           
  
          </div>


        );
      }
    }
];

//JSON DATA
const [productData,setProductData] = useState({
  "Product_ID": 0,
  "Product_name": "",
  "Product_category": "",
  "Product_stocks": 0,
  "Product_price": 0
})
 
//Data
const [Data,setData] = useState()

//search data variable
const [search,setSearch] = useState("") 


// Filtering data 
const filtered = !search ? 
Data 
: Data.filter((person) => 
    person.Product_name.toLowerCase().includes(search.toLowerCase())
  );

// http request api
  useEffect(() => {

    axios.get('http://localhost/REACTJS/cognate1%20api/ProductdataAPI.php')
    .then(res=>setData(res.data))
    .catch(err=>console.log(err))

 });


  return (
    <div>

      <Grid>

        {/* Search bar */}
        <Grid item md={12} padding={2}>
          
          <Grid

          container
          direction="row"
          justifyContent="center"
          alignItems="center"
          style={{
            backgroundColor:'white',
            border: '2px solid black',
            borderRadius: '5px'
          }}
          padding={2}>

         {/* Search bar */}
          <CTextField 
          fullWidth 
          value = {search} 
          onChange={e =>{ 
            setList_data(true)
            setSearch(e.target.value)}}  
          placeholder='Search by product name' 
          variant='standard'
          margin='normal'
          disabled={ !Data ? true : false}
          InputProps={{
            endAdornment: (
              <InputAdornment position="start">
      
                <SearchOutlinedIcon fontSize='large' />
             
              </InputAdornment>
            ),
          }}
          
          
          />

             {/* Button bar */}
            <Button variant='contained'
            disabled={Data ? false : true}
            onClick={e=>
            {
              e.preventDefault();
              list_data ? setList_data(false) : setList_data(true)
            
            
            }}> show data list</Button>
          </Grid>

        </Grid>
      
{/* Datable container */}
      <Grid item md={12} padding={2}>

      <Collapse in={list_data}> {/* Pang collapse */}
        
        <Grid
        container

        direction="row"
        justifyContent="center"
        alignItems="center"
      
        padding={2}>

          <Box
          sx={{
            backgroundColor:'white',
            border:'2px solid #9B3C21',
            borderRadius: 2,
            height: 550,
            width: 500,
          }}
         >
          


            <DataGrid
              columns={columns}
              rows={filtered} // data na pinasok yung data 
              getRowId={(rows)=> rows.Product_ID}
              pageSize={10}
              onSelectionModelChange={(ids) => {
                setId(parseInt(ids))
              }}

            />

            
          </Box>
           
        </Grid>
      </Collapse> 
    
    </Grid>

   <Collapse in={list_data ? false : true}>
  

    <Grid item md={12} padding={2}>

      <Grid
      container
     // onClick={e=>setList_data(false)}
      direction="row"
      justifyContent="center"
      alignItems="center"
      spacing={1}
      padding={2}
      style={{
        backgroundColor:'white',
        border:'2px solid black'
      }}>


{/* Container Product name */}
        <Grid item md={6} xs={12} >
          

          {/* Label product name */}
          <Typography variant='h6' margin={1}>Product name</Typography>

         {/* Textfield product name */}
          <CcTextField
          fullWidth 
          label={ !id ? "No data found" : 
            filtered.filter(index=>index.Product_ID === id)
            .map(ge=>ge.Product_name)
          } 
         />

        </Grid>

{/* Container Product Category */}
        <Grid item md={6} xs={12}>
         
        {/* Label product name */}
          <Typography variant='h6' margin={1}>Product Category</Typography>

        {/* Textfield Product category */}
          <CcTextField 
          fullWidth 
            label={ !id ? "No data found" : 
            filtered.filter(index=>index.Product_ID === id)
            .map(ge=>ge.Product_category)
          }/>

          <CcTextField 
          fullWidth
          value={
            ()=>{
              setProductData(!id ? "No data found" : 
              filtered.filter(index=>index.Product_ID === id)
              .map(ge=>ge.Product_category))
              productData.Product_name 
            }
            
          }
          
          />
         
        </Grid>

{/* Container Product stocks */}
        <Grid item md={6} xs={12}>

          {/* Label product name */}
          <Typography variant='h6' margin={1}>Product Stocks</Typography>

          {/* Textfield Product stocks */}
          <CcTextField 
          type='number' 
          fullWidth 
          label={ !id ? "No data found" : 
          filtered.filter(index=>index.Product_ID === id)
          .map(ge=>ge.Product_stocks)
          }/>

        </Grid>

{/* Container Product price */}
        <Grid item md={6} xs={12}>

          {/* Label product name */}
          <Typography variant='h6' margin={1}>Product Price</Typography>

          {/* Textfield Product price */}
          <CcTextField 
          type='number' 
          fullWidth 
          label={ !id ? "No data found" : 
          filtered.filter(index=>index.Product_ID === id)
          .map(ge=>ge.Product_price)
          }/>

        </Grid>


        <Grid item>
          <Button variant='contained'  > Save</Button>
        </Grid>


      </Grid>
     
      

    </Grid>

   
        
       
    </Collapse>
    









  </Grid>




    </div>
  )
}

export default Stocks
