import { Button, Grid, TextField } from '@mui/material'
import axios from 'axios'
import React, { useState } from 'react'

const Testuploadimage = () => {

    const [upload, setUpload] = useState({
        file:[],
        filepreview: null

    })

    // const handleInputChange = (event) => {
    //     setuserInfo({
    //       ...userInfo,
    //       file:event.target.files[0],
    //       filepreview:URL.createObjectURL(event.target.files[0]),
    //     });
    
    //   }

    const submit =  (e) =>{
        e.preventDefault()
        const formdata = new FormData(); 
        formdata.append('avatar', upload.file);
    
        axios.post("http://localhost:8080/imageupload", formdata,{   
                headers: { "Content-Type": "multipart/form-data" } 
        })
        .then(res => { // then print response status
          console.warn(res);
          
    
        })
      }

  return (
    <div>
        <Grid container paddingTop={20}>

    
        <TextField type='file' margin='normal' size='medium' fullWidth variant='outlined'
        onChange={e=>{
            setUpload({file:e.target.files[0], filepreview:URL.createObjectURL(e.target.files[0])}) 
        }}/>
        <Button fullWidth variant='contained'>upload</Button>
        </Grid>
    </div>
  )
}

export default Testuploadimage
