import React, { useEffect, useState } from 'react'
import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import DialogTitle from '@mui/material/DialogTitle';
import { Typography } from '@mui/material';



const Verification_code = () => {

const [disable,setDisbale] =  useState(true)

const [number, setNumber] = useState(60)
useEffect(
  () => 
  { 
   const interval = setInterval(()=>{

    if (number !== 0) setNumber(number - 1)
    },1000)

    return () => clearInterval(interval)
  }

  )

  return (
    <div>
      <Dialog open={true} >
        <DialogTitle>High Resolution</DialogTitle>
        <DialogContent>
          <DialogContentText>
          To submit verification to your account, please click the send button and enter the verification code in the textbox.
          </DialogContentText>
          <TextField
            autoFocus
            margin="normal"
            id="name"
            label="Authentrication Code"
            type="number"
            fullWidth
            variant="standard"
          />
          <DialogContentText paddingY={1}>
          verification code expired in {number}.
          </DialogContentText>
              
          <Button disabled={true} variant='outlined' onClick={e=>e.preventDefault() }>send code</Button> 
      
        </DialogContent>
        <DialogActions>
          <Button  onClick={e=>e.preventDefault() }>Cancel</Button>
        </DialogActions>
      </Dialog>
    </div>
  )
}

export default Verification_code
