// Library components

import { Grid, 
        InputAdornment,
        Typography,
        Fab,
        IconButton,
        Collapse,
        Button,
        FormControlLabel,
        Switch,
        Box,
        Alert,
        RadioGroup,
        Radio,
        Select,
        Chip,
        MenuItem,
        Checkbox,
        ListItemText,
} from '@mui/material'
import { CTextField } from '../products/Products' 
import { CcTextField } from '../Stocks';
import React, { useEffect, useState } from 'react'
import AdapterDateFns from '@mui/lab/AdapterDateFns';

// Icons
import SearchOutlinedIcon from '@mui/icons-material/SearchOutlined';
import AddIcon from '@mui/icons-material/Add';
import DeleteOutlineOutlinedIcon from '@mui/icons-material/DeleteOutlineOutlined';
import ClearIcon from '@mui/icons-material/Clear';
import moment from 'moment'

// Routes
import { useNavigate } from 'react-router-dom';
import { DataGrid } from '@mui/x-data-grid';
import axios from 'axios';


import LOGO  from '../../icons/user.png'
import { LocalizationProvider, MobileDatePicker } from '@mui/lab';



const Invoice = () => {
// Navigation
let NavS = useNavigate();


// Column header
const columns = [

  // ID Column
  { field: 'Customer_ID', 
    headerName: 'ID', 
    width: 70 ,
    headerAlign: 'center',

    renderCell: (cellValues) => {
      return (
        <div
          style={{
            color: "black",
            fontSize: 18,
            width: "100%",
            textAlign: "center"
          }}
        >
          {cellValues.value}
        </div>
      );
    }
  },

  // ID Column
  { field: 'Customer_name', 
    headerName: 'name', 
    width: 200,
    headerClassName: 'super-app-theme--header',
    headerAlign: 'center', 

    renderCell: (cellValues) => {
      return (
        <div
          style={{
            color: "black",
            fontSize: 18,
            width: "100%",
            textAlign: "center"
          }}
        >
          {cellValues.value}
        </div>
      );
    }
  
  },

  { field: 'Customer_address', 
    headerName: 'Address', 
    width: 130,
    headerAlign: 'center', 

    renderCell: (cellValues) => {
      return (
        <div
          style={{
            color: "black",
            fontSize: 18,
            width: "100%",
            textAlign: "center"
          }}
        >
          {cellValues.value}
        </div>
      );
    }

  
  },

  { field: 'Customer_email', 
    headerName: 'email', 
    width: 130,
    headerAlign: 'center', 

    renderCell: (cellValues) => {
      return (
      <div
      style={{
        color: "black",
        fontSize: 18,
        width: "100%",
        textAlign: "center"
        }}
      >
        {cellValues.value}
        </div>
        );
    }
  },

  { field: 'Customer_cell', 
    headerName: 'Cellphone number', 
    width: 150,
    headerAlign: 'center', 

    renderCell: (cellValues) => {
      return (
        <div
          style={{
            color: "black",
            fontSize: 18,
            width: "100%",
            textAlign: "center"
          }}
        >
          {cellValues.value}
        </div>
      );
    }
  },

  { field: 'Customer_purchase', 
  headerName: 'product purchase', 
  width: 150,
  headerAlign: 'center', 

  renderCell: (cellValues) => {
    return (
      <div
        style={{
          color: "black",
          fontSize: 18,
          width: "100%",
          textAlign: "center"
        }}
      >
        {cellValues.value}
      </div>
    );
  }
},

{ field: 'Customer_date', 
headerName: 'date', 
width: 150,
headerAlign: 'center', 

renderCell: (cellValues) => {
  return (
    <div
      style={{
        color: "black",
        fontSize: 18,
        width: "100%",
        textAlign: "center"
      }}
    >
      {cellValues.value}
    </div>
  );
}
},

{ field: 'Customer_total', 
headerName: 'total', 
width: 130,
headerAlign: 'center', 

renderCell: (cellValues) => {
  return (
    <div
      style={{
        color: "black",
        fontSize: 18,
        width: "100%",
        textAlign: "center"
      }}
    >
      {cellValues.value}
    </div>
  );
}
},

{ field: 'Customer_payment', 
headerName: 'status', 
width: 130,
headerAlign: 'center', 

renderCell: (cellValues) => {
  return (
    <div
      style={{
        color: "black",
        fontSize: 18,
        width: "100%",
        textAlign: "center"
      }}
    >
      {cellValues.value}
    </div>
  );
}
},
  
// delete
  {
    field: 'action',
    headerName: 'Action',
    width: 130,
    headerAlign: 'center', 
    renderCell: () => {
      return (
        <div
          style={{
            width: "100%",
            textAlign: "center",
            // marginRight: '2'
          }}
        >
         

        <IconButton color="primary" aria-label="delete">
            <DeleteOutlineOutlinedIcon/>
        </IconButton>
         
        </div>


      );
    }
  }

];
//search data variable
const [search,setSearch] = useState("")

// Data 
const [Data,set] = useState()

// Edit true or not
const [edit,setEdit] = useState(true)

// Product purchase
const [selectname, setSelectname] = useState([])

// Filtering data 
const filtered = !search ? 
Data 
: Data.filter((person) => 
    person.Customer_name.toLowerCase().includes(search.toLowerCase())
  );

  // Menu props
const ITEM_HEIGHT = 48;
const ITEM_PADDING_TOP = 8;

const MenuProps = {
  PaperProps: {
    style: {
      maxHeight: ITEM_HEIGHT * 4.5 + ITEM_PADDING_TOP,
      width: 250,
    },
  },
};

 // Status
 const [status,setStatus] = useState({
  "status" : "paid",
   "check" : null
})

// Show success
const [show_success,setShow_success] = useState()

// Show error
const [show_error,setShow_error] = useState()

  //JSON
const [data, setData] = useState({
    "Customer_ID" : 0,
    "Customer_name": "",
    "Customer_email": "",
    "Customer_cell": "",
    "Customer_address": "",
    "Customer_purchase": [],
    "Customer_date": "",
    "Customer_total": 0,
    "Customer_payment": ""
  })

// Htttp get request
useEffect(() => {
  axios.get('http://localhost/REACTJS/cognate1%20api/CustomerdataAPI.php')
.then(res=>set(res.data))
.catch(err=>console.log(err)) 
})

  // Delete customerdata
const delete_customerdata = (param) => {
  
    if(param.field === "action")
    {
      axios.post("http://localhost/REACTJS/cognate1%20api/Delete_CustomerdataAPI.php", { "Customer_ID": param.id } )
      .then(res => console.log(res.data))
    }
  
}

// Date ITO
const [value,setValue] = useState(
  new Date()
)
 

// Data product
const [dataproduct,setDataproduct] = useState([])

// filtered fetch product name
const name = !dataproduct ? 
"empty" 
: dataproduct.map(name=>name.Product_name )


// Customer product name
const addproductname = () =>
{
  axios.get('http://localhost/REACTJS/cognate1%20api/ProductdataAPI.php')
  .then(res=> setDataproduct(res.data))
}

  // Update customerdata
const update_customerdata = e => {

  e.preventDefault()  
     
  axios.put("http://localhost/REACTJS/cognate1%20api/Update_CustomerdataAPI.php",  {
    "Customer_name": data.Customer_name,
    "Customer_email": data.Customer_email,
    "Customer_cell": data.Customer_cell,
    "Customer_address": data.Customer_address,
    "Customer_purchase": data.Customer_purchase.toString(),
    "Customer_date": moment(value,"mm-dd-yyyy").format().split('T')[0],
    "Customer_total": data.Customer_total,
    "Customer_payment":  data.Customer_payment
  })
  .then(res => 
    {
      setShow_success(res.data[0].Result)
      setEdit(res.data[0].Result) 
      setShow_error(res.data[0].Result)
    })
 
}


 // Edit customerdata
const edit_customerdata = (param) => {
  setData({
    Customer_ID : param.id,
// Customer name
    Customer_name: String(filtered.filter(index=>index.Customer_ID === param.id)
    .map(ge=>ge.Customer_name)),

// Customer email
    Customer_email: String(filtered.filter(index=>index.Customer_ID === param.id)
    .map(ge=>ge.Customer_email)),

// Customer cell
    Customer_cell: parseInt(filtered.filter(index=>index.Customer_ID === param.id)
    .map(ge=>ge.Customer_cell)),

// Customer address
    Customer_address: String(filtered.filter(index=>index.Customer_ID === param.id)
    .map(ge=>ge.Customer_address)),

// Customer purchase
    Customer_purchase: String(filtered.filter(index=>index.Customer_ID === param.id)
    .map(ge=>ge.Customer_purchase)).split(','),

// Customer date
    Customer_date: Date(filtered.filter(index=>index.Customer_ID === param.id)
    .map(ge=>ge.Customer_date)),

// Customer total
    Customer_total: parseInt(filtered.filter(index=>index.Customer_ID === param.id)
    .map(ge=>ge.Customer_total)),

// Customer payment
    Customer_payment: String(filtered.filter(index=>index.Customer_ID === param.id)
    .map(ge=>ge.Customer_payment)),
  })
  // setSelectname(String(filtered.filter(index=>index.Customer_ID === param.id)
  // .map(ge=>ge.Customer_date)).split(','))

  setEdit(false)

  filtered.filter(index=>index.Customer_payment === "Paid") ? 
  setStatus({...status,check: true}) : setStatus({...status,check: false})

  addproductname()
// console.log(status.check)


}
  return (
    <div>
{/* Alert Success */}
      <Collapse in={show_success}>
          <Alert severity="success"
          onClose={()=>{
            
            setShow_success(false) 
          }}
          >
            Data has been updated
          </Alert>
        </Collapse>

{/* Alert Error */}
<Collapse in={show_error ? false : true}>
          <Alert severity="error"
          onClose={()=>{
            
            setShow_error(true) 
          }}
          >
            ATTENTION! textfield should not be empty!
          </Alert>
        </Collapse>


      <Collapse in={edit}>
      {/* Whole Container */}
        <Grid container spacing={2}>
 
        
{/* Search bar container */}
          <Grid item md={9} xs={12}>

          

            {/* Para may padding */}
           <Grid 
           container 
           padding={5}
           direction="row"
           justifyContent="center"
           alignItems="flex-start">

             {/* Search bar */}
              <CTextField 
              variant='standard' 
              fullWidth 
              placeholder='Searchbar'
              value={search}
              onChange={e=>setSearch(e.target.value)}

              InputProps={{
                endAdornment: (
                  <InputAdornment position="start">
    
                    <SearchOutlinedIcon fontSize='large' />
                 
                  </InputAdornment>
                ),
              }}
              />

          
           </Grid>
         
    
          </Grid>

          {/* Conatainer button */}
          <Grid item md={3} xs={12}>

            {/* Container para may padding */}
            <Grid 
            container 
            padding={5}           
            direction="row"
            justifyContent="center"
            alignItems="flex-start">

              {/* Button add */}
              <Fab 
              color="primary" 
              variant='extended' 
              aria-label="add" 
              onClick={e=>NavS("/Mainpage/Invoice/AddData")} 
              >
                <AddIcon />
                add data
              </Fab>

            </Grid>
            
          </Grid>



          {/* Container for table */}
          <Grid item md={12}>
            
            {/* Para may padding */}
            <Grid 
            container             
            direction="row"
            justifyContent="flex-start"
            alignItems="flex-start"
            padding={5}>

            <Typography variant='h6'>
             double click the row to edit a specific data
           </Typography>

              {/* pang size at background color */}
              <Grid 
              container
              style={{
                height:550 ,
                width:'100%',
                backgroundColor: 'White',
                border:'2px solid black',
                borderRadius: 3               
              }}
              >



                {/* Data table */}
                <DataGrid
                columns={columns}
                getRowId={(rows)=> rows.Customer_ID}
                rows={filtered} // data na pinasok yung data 
                  
                pageSize={10}
                onCellClick={delete_customerdata}
                onCellDoubleClick={edit_customerdata}
                />

              </Grid>
           
          
            </Grid>

          </Grid>

 
        </Grid>
        
      </Collapse>
        
{/* Edit profile */}
          <Collapse in={edit ? false : true}>

          {/* // Whole Container */}
    <Grid container padding={6} paddingTop={2} >

{/* Container para sa button paar may padding */}
      <Grid container padding={2}>
                      
        {/* Button cancel */}
        <Fab 
        color="primary"  
        aria-label="add" 
      onClick={e=>setEdit(true)} 
        >
          <ClearIcon />
 
        </Fab>
        <Typography padding={1} paddingLeft={2} variant='h4'>
        Edit data profile
      </Typography>

      </Grid>


{/* Container for all components */}
      <Grid 
      container
      direction="row"
      justifyContent="center"
      alignItems="flex-start" 
      style={{
        backgroundColor:"white",
        border:"2px solid black",
        borderRadius: 5
      }}>

{/* CONTAINER FOR ICON LOGO */}
        <Grid item md={5} xs={12}>

          <Grid 
          container 
          padding={2}
          paddingTop={6}
          direction="row"
          justifyContent="center"
          alignItems="flex-start">

          {/* Picturebox */}
              <Box
              fixed
              component='img'
              marginY={2}

              sx={{
                width: 160,
                height: 160,
              }}
              src={LOGO} />

              <Grid md={12} padding={2}>
              {/* Name */}
              <Typography variant='h6'> Name:</Typography>

              <CcTextField 
              fullWidth 
              margin='normal'
              type='text'
              value={data.Customer_name}
              onChange={e=> setData({...data, Customer_name: e.target.value})}
              
              />

              {/* Email */}
              <Typography variant='h6'> Email:</Typography>

              <CcTextField  
              fullWidth 
              margin='normal'
              type='text'
              value={data.Customer_email}
              onChange={e=> setData({...data, Customer_email: e.target.value})}
              />

              {/* Cellphone number */}
              <Typography variant='h6'> Cellphone number:</Typography>
              
              <CcTextField  
              fullWidth 
              margin='normal'
              type='number'              
              value={data.Customer_cell}
              onChange={e=> setData({...data, Customer_cell: String(e.target.value)})}
              />

              
              {/* Address */}
              <Typography variant='h6'> Address:</Typography>
              <CcTextField  
              fullWidth 
              margin='normal' 
              multiline
              type='text'
              value={data.Customer_address}
              onChange={e=> setData({...data, Customer_address: String(e.target.value)})}
              
              /> 


            </Grid>

          </Grid>

          

        </Grid>


        {/* Components details */}
        <Grid container item md={7} xs={12} padding={2} paddingTop={13}>
          
{/* Purchase product */}
              <Typography variant='h6'> Purchase product:</Typography>
              
              <Select
              multiple
              fullWidth
              value={data.Customer_purchase}
           
              onChange={(e)=>{

                setData( {
                  ...data, Customer_purchase: e.target.value
                }
                  
                  // typeof e.target.value === 'string' ? e.target.value.split(',') 
                  // : e.target.value
                )


              }}
              renderValue={(selected) => (
              <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 0.5 }}>
                {selected.map((value) => (
                <Chip key={value} label={value} color='primary'/>
              ))}
              </Box>
              )}
              MenuProps={MenuProps}
              error={show_error? false : true}
              onClick={()=>setShow_error(true)}
              >

                {name.map(names=> (
                  <MenuItem
                  key={names}
                  value={names}
                  >
                    
                    <Checkbox checked={data.Customer_purchase.indexOf(names) >= 0} />
                    <ListItemText primary={names} />
                
                  </MenuItem>
                ))
     
                }

              </Select>


{/* Date */}

<Typography marginTop={2} variant='h6'> Date:</Typography>
      <Grid container>


<LocalizationProvider  dateAdapter={AdapterDateFns}>

        <MobileDatePicker
        
          error={show_error? false : true}
          inputFormat="MM/dd/yyyy"
          value={value}
          onChange={(newValue) => setValue(newValue) }
          renderInput={(params) => <CcTextField margin='normal' {...params} 
          onClick={()=>{

            setShow_error(true)
          }}
          />}
          
        />

    </LocalizationProvider>
    </Grid>
{/* Total product */}
              <Typography variant='h6'> Total:</Typography>
              
              <CcTextField  
              fullWidth 
              margin='normal'
              type='number'
              value={data.Customer_total}
              onChange={e=> setData({...data, Customer_total: String(e.target.value)})}
              
              />

   {/* Radio Button for payment status */}
   <RadioGroup
          row
          value={data.Customer_payment}
          onChange={e => {
            setData({...data,Customer_payment: e.target.value })
          }}
        
          >
          <FormControlLabel  value="Paid" control={<Radio />} label="Paid" />
          <FormControlLabel value="Pending" control={<Radio />} label="Pending" />
        </RadioGroup>
            

        </Grid>



        {/* Button update */}

        <Grid
        item
        container
        md={12}
        padding={5}
        direction="row"
        justifyContent="center"
        alignItems="flex-start">

        {/* Add item button */}
          <Button 
          onClick={update_customerdata}
          variant='contained' 
          size='large' style={{
          borderRadius: 50
            
          }}>
            Update data
          </Button>

        </Grid>

   

      </Grid>

    </Grid>
  </Collapse>

    </div>
  )
}

export default Invoice
