import React, {  useEffect, useState } from 'react'
import axios from 'axios'
import { IconButton, ImageList, ImageListItem, ImageListItemBar } from '@mui/material'

import InfoIcon from '@mui/icons-material/Info';


const TEST = () => {

  // fetch data
const [data,setData] = useState([])

useEffect(() => {

  // if it is render
  let isCancel = true

  axios.get("http://localhost/REACTJS/cognate1%20api/ProductdataAPI.php")
  .then(res=>
    {
      if(isCancel)
      {
        setData(res.data)
      }
      
    }
  )

  // return value if this page is not render
  return () => isCancel = false 
})



  return (
    <div>

          <ImageList sx={{ width: 500, height: 450 }} cols={1} rowHeight={164} >

            {/* Mapping image */}
          {data.map(

            // For each process
            item => (

              <ImageListItem key={item.Product_ID}>

                {/* Image property */}
                <img
                  src={item.Product_image}
                  alt={item.Product_name}
                  loading="lazy"
                />


                <ImageListItemBar
                  title={item.Product_image}
                  actionIcon={
                  
                  <IconButton
                    sx={{ color: 'rgba(255, 255, 255, 0.54)' }}
                  >
                    <InfoIcon />
                  </IconButton>
            }
          />

            </ImageListItem>

            

          )
          
        )}

        </ImageList>
        
        
        
     



    </div>
  )
}

export default TEST
