import { AppBar, Button, Container, Grid, Paper, TextField, Typography } from '@mui/material'
import { borderRadius, Box, color, maxWidth } from '@mui/system'
import { styled } from '@mui/material/styles';
import React from 'react'
import Logo from './Logo.ico'





// Custom Textbox
const CTextField = styled(TextField)({

 
    '& .MuiOutlinedInput-root': {
      '& fieldset': {
        borderColor: '#9B3C21',
      },
      '&:hover fieldset': {
        borderColor: '#9B3C21',
      },
  
    },
  });
  

const Login = () => {
  return (
    <div>

{/* Buong container */}
         <Grid container spacing={2} padding={2} style={{minHeight:'100vh'}}                 
         direction="row"
         justifyContent="center"
         alignItems="center">

{/* Logo Column */}
            <Grid item xs={12} sm={12} md = {6} lg={6} zeroMinWidth >

{/* Container Logo */}
            <Grid container
                direction="column"
                justifyContent="center"
                alignItems="center">

{/* Logo Icon */}
                 <img src={Logo} alt='Logo' style={{minHeight:'30vh', objectFit:'cover'}}/>
                
            </Grid>

        </Grid>

{/* Login Column */}
            <Grid item xs={12} sm={12} md = {6} lg={6} zeroMinWidth >
  
{/* Container para cool */}
            <Grid container

                Container
                style={{
                  backgroundColor:'white',
                  borderRadius: 5,
                  border:'3px solid black'}}>

{/* header na brown */}
                    <AppBar position="static" style={{minHeight:'40'}}>asdads</AppBar>

                    <Grid container 
                    direction="column"
                    justifyContent="center"
                    alignItems="center"
                    padding={2}
                    Container>

                    

{/* Textbox */}
                    <CTextField label= 'asds' placeholder='asdasd' fullWidth margin='normal'/>
                    <CTextField label= 'asds' placeholder='asdasd' fullWidth margin='normal'/>

{/* hyperlink */}
                    <a href=''>asdasdasd</a>
                    <br/> 

{/* Login Button */}
                    <Button>Login</Button>

                    </Grid>
                        
              </Grid>
                    
            </Grid>

 {/* Buong container */}               
         </Grid>
         
         
    </div>
  )
}

export default Login
