import {  
  Typography, 
  IconButton, 
  Toolbar, 
  Drawer, 

  Grid,
  Box,
  ButtonGroup,
  Button,

} from '@mui/material';

import React, { useState } from 'react'
import { 
  styled, 
} from '@mui/material/styles';

import MuiAppBar from '@mui/material/AppBar';

///ICONS

// import LOGO  from '../pages/icons8_male_user.ico'
import MenuRoundedIcon from '@mui/icons-material/MenuRounded';
import AnalyticsOutlinedIcon from '@mui/icons-material/AnalyticsOutlined';
import AddShoppingCartOutlinedIcon from '@mui/icons-material/AddShoppingCartOutlined';
import BusinessCenterOutlinedIcon from '@mui/icons-material/BusinessCenterOutlined';
import AutoGraphOutlinedIcon from '@mui/icons-material/AutoGraphOutlined';
import FeedOutlinedIcon from '@mui/icons-material/FeedOutlined';

// COMPONENTSS
import Products from './Components/Products';
import Salesreport from './Components/Salesreport';
import Dashboard from './Components/Dashboard';
import Stocks from './Components/Stocks';
import Invoice from './Components/Invoice';

const drawerWidth = 240;
///Butoons


// Cutom Button
const CButton = styled(Button)({
  boxShadow: "none",
  textTransform: "none",
  fontSize: 18,
  padding: "6px 12px",
  lineHeight: 2.5,

});




// Custom Header
const AppBar = styled(MuiAppBar, {
  shouldForwardProp: (prop) => prop !== 'open',
})(({ theme, open }) => ({
  transition: theme.transitions.create(['margin', 'width'], {
    easing: theme.transitions.easing.sharp,
    duration: theme.transitions.duration.leavingScreen,
  }),
  ...(open && {
    width: `calc(100% - ${drawerWidth}px)`,
    marginLeft: `${drawerWidth}px`,
    transition: theme.transitions.create(['margin', 'width'], {
      easing: theme.transitions.easing.easeOut,
      duration: theme.transitions.duration.enteringScreen,
    }),
  }),
}));


// function

const Mainpage = () => {
  //Hook for sidebar true or false
  const [open ,setOpen] = useState(false)
    //Hook for showing pages
  const [active,setActive] = useState("")
 
// CUSTOM BUTTON
const buttons = [

  // Button Dashboard
  <IconButton  color="secondary" onClick={(e)=>{e.preventDefault(); setActive("Dashboard")}}>
    {/* Icon */}
    <AnalyticsOutlinedIcon  fontSize='large'/> 
    {/* Text */}
    <Typography  variant='h6'marginX={3} >Dashboard</Typography>
  </IconButton>,

  // Button Stocks
  <IconButton fullwidth color="secondary" onClick={(e)=>{e.preventDefault(); setActive("Stocks")}}>
    <AddShoppingCartOutlinedIcon  margin='dense' fontSize='large'/>
    <Typography variant='h6'marginX={5.5} >Stocks</Typography>
  </IconButton>,


    // Button Products
  <IconButton fullwidth color="secondary" onClick={(e)=>{e.preventDefault(); setActive("Products")}}>
    <BusinessCenterOutlinedIcon  margin='dense' fontSize='large'/>
    <Typography variant='h6'marginX={4.2} >Products</Typography>
  </IconButton>,

    // Button Sales Report
  <IconButton fullwidth color="secondary" onClick={(e)=>{e.preventDefault(); setActive("Salesreport")}}>
    <AutoGraphOutlinedIcon  margin='dense' fontSize='large'/>
    <Typography noWrap variant='h6' marginX={2}  >Sales Report</Typography>
  </IconButton>,

    // Button Invoice
  <IconButton fullwidth color="secondary" onClick={(e)=>{e.preventDefault(); setActive("Invoice")}}>
    <FeedOutlinedIcon  margin='dense' fontSize='large'/>
    <Typography variant='h6'marginX={5.5} >Invoice</Typography>
  </IconButton>,



];

  return (
<div>



  <AppBar position='fixed' open={open} 
  onClick={() => {
    if(open){
      setOpen(false)
    }
  }}>
    

      <Toolbar>

{/*Button for side bar  */}
      <IconButton
            size="large"
            edge="start"
            color="inherit"
            aria-label="menu"
            sx={{ mr: 2, ...(open && { display: 'none' }) }}
           onClick={(e) => {
             e.preventDefault()
             setOpen(true)
           }}>
{/* ICON */}
            <MenuRoundedIcon fontSize='large' />

          </IconButton>
{/* TEXT */}
          <Typography variant='h5'>{active}</Typography>

{/* SIDE BAR */}
      <Drawer
// permanent
      variant="persistent"
      anchor="left"
      open={open}
      sx={{
        
        width: drawerWidth,
        flexShrink: 0,
        '& .MuiDrawer-paper': {
          width: drawerWidth,
          boxSizing: 'border-box',
        },
      }}>
        
        <Grid           
          item 
  
          container
          direction="column"
          justifyContent="center"
          alignItems="center">
            
            <Box
            fixed
            component='img'
            marginY={8}

            sx={{
              width: 200,
              height: 100,
            }}
            src={LOGO} />

            {/* <Typography variant='body2'> Frances Bryan Maneclang</Typography> */}
            <br/>
            <br/>
            <br/>

        </Grid>



                <ButtonGroup
                fullwidth
                alignItems='flex-start'
                orientation="vertical"
                aria-label="vertical contained button group"
                variant="outlined">



                {buttons}

                </ButtonGroup>
      

      
  
      </Drawer>
    </Toolbar>
  </AppBar>
  <br/>
  <br/>
  <br/>
  <br/>
  {active === "Dashboard" && <Dashboard/>}
  {active === "Stocks" && <Stocks/>}
  {active === "Products" && <Products/>}
  {active === "Salesreport" && <Salesreport/>}
  {active === "Invoice" && <Invoice/>}
    



    </div>

  )
}

export default Mainpage
