import { 
  Fab, 
  Grid, 
  IconButton, 
  InputAdornment, 
  MenuItem, 
  Select, 
  TextField, 
  Typography ,

} from '@mui/material'

import { styled } from '@mui/material/styles';
import {React , useEffect, useState }from 'react';
import axios from 'axios';

import AddIcon from '@mui/icons-material/Add';
import DeleteOutlineOutlinedIcon from '@mui/icons-material/DeleteOutlineOutlined';
import SearchOutlinedIcon from '@mui/icons-material/SearchOutlined';

//npm i @mui/x-data-grid
import { DataGrid } from '@mui/x-data-grid';
import {useNavigate } from 'react-router-dom';






const CTextField = styled(TextField)({
  '& .MuiInputBase-input': {

    position: 'relative',
    fontSize: 20,
    padding: '10px 12px',

  },

})
// MAIN FUNCTIOM 
const Products = () => {
// random

const [add,setAdd] = useState()


let NavS = useNavigate();

// Category data variable
const [category, setCategory] = useState("None")

//Data from API
const [Data,setData] = useState() 

//search data variable
const [search,setSearch] = useState("") 


// Filtering data 
const filtered = !search ? 
Data 
: Data.filter((person) => 
    person.Product_name.toLowerCase().includes(search.toLowerCase())
  );


  // http request api
useEffect(() => {

     axios.get('http://localhost/REACTJS/cognate1%20api/ProductdataAPI.php')
     .then(res=>setData(res.data))
     .catch(err=>console.log(err))

 
    
  });



// Column header

  const columns = [

    // ID Column
    { field: 'Product_ID', 
      headerName: 'ID', 
      width: 70 ,
      headerAlign: 'center',

      renderCell: (cellValues) => {
        return (
          <div
            style={{
              color: "black",
              fontSize: 18,
              width: "100%",
              textAlign: "center"
            }}
          >
            {cellValues.value}
          </div>
        );
      }
    },

    // ID Column
    { field: 'Product_name', 
      headerName: 'name', 
      width: 200,
      headerClassName: 'super-app-theme--header',
      headerAlign: 'center', 

      renderCell: (cellValues) => {
        return (
          <div
            style={{
              color: "black",
              fontSize: 18,
              width: "100%",
              textAlign: "center"
            }}
          >
            {cellValues.value}
          </div>
        );
      }
    
    },

    { field: 'Product_category', 
      headerName: 'category', 
      width: 130,
      headerAlign: 'center', 

      renderCell: (cellValues) => {
        return (
          <div
            style={{
              color: "black",
              fontSize: 18,
              width: "100%",
              textAlign: "center"
            }}
          >
            {cellValues.value}
          </div>
        );
      }
  
    
    },

    { field: 'Product_stocks', 
      headerName: 'stocks ', 
      width: 130,
      headerAlign: 'center', 

      renderCell: (cellValues) => {
        return (
        <div
        style={{
          color: "black",
          fontSize: 18,
          width: "100%",
          textAlign: "center"
          }}
        >
          {cellValues.value}
          </div>
          );
      }
    },

    { field: 'Product_price', 
      headerName: 'price', 
      width: 130,
      headerAlign: 'center', 

      renderCell: (cellValues) => {
        return (
          <div
            style={{
              color: "black",
              fontSize: 18,
              width: "100%",
              textAlign: "center"
            }}
          >
            {cellValues.value}
          </div>
        );
      }
    },

    {
      field: 'action',
      headerName: 'Action',
      width: 130,
      headerAlign: 'center', 
      renderCell: () => {
        return (
          <div
            style={{
              width: "100%",
              textAlign: "center",
              // marginRight: '2'
            }}
          >
           

          <IconButton color="primary" aria-label="delete"
          checkboxSelection
             onSelectionModelChange={(ids) => {
              setAdd(ids);
            }}>
              <DeleteOutlineOutlinedIcon/>
          </IconButton>
           
  
          </div>


        );
      }
    }

  ];
 
  return (
    <div>

{/* Whole Container  */}
<Grid container spacing={2}>

{/* Products */}
    <Grid container item xs={12} md={10}>
      <Typography variant='h3' marginY={2} marginLeft={2}> Products</Typography>
      {/* <h1>{Data[2].Product_name}</h1> */}
      <h1>{add}</h1>
    </Grid>

{/* Button container style */}
    <Grid container item xs={12} md={2}>

{/* Button container para may margin na maayos */}
      <Grid
        container
        direction="row"
        justifyContent="flex-start"
        alignItems="center"
        margin={2}>


            <Fab color="primary" variant='extended' aria-label="add" onClick={e=>NavS("/Mainpage/Products/AddItem")} >
                <AddIcon />
            add item
            </Fab>

      </Grid>

    </Grid>

{/* Search bar and data container */}
    <Grid
      container
      item
      xs={12}
      direction="row"
      justifyContent="center"
      alignItems="flex-start">

      {/* Container para maayos ang margin */}
        <Grid container item xs={12} marginLeft={3} marginRight={3} style={{width: '1000'}}>

   {/* Search bar */}
          <CTextField 
          fullWidth 
          value = {search} 
          onChange={e => setSearch(e.target.value)}  
          placeholder='Search by product name' 
          variant='standard'
          InputProps={{
            endAdornment: (
              <InputAdornment position="start">

                <SearchOutlinedIcon fontSize='large' />
             
              </InputAdornment>
            ),
          }}
          
          
          />

   {/* CATEGORY STOCKS */}
          <Grid container item xs={2} md={1} margin={2}>
          {/* <Select
          value={category}
          label="category"
          defaultValue="{category}"
          onChange={e => setCategory(e.target.value)}  
          
        >

          <MenuItem >Ten</MenuItem>
          <MenuItem  >Twenty</MenuItem>
          <MenuItem>Thirty</MenuItem>
        </Select> */}

          </Grid>
         
          {/* Datagrid */}
          <Grid
          container
          marginLeft={2}
          marginRight={4}
          marginBottom={3}
          style={{height:550 ,
          width:'100%',
          backgroundColor: 'White',
          border:'2px solid black',
          
          
          }}>

               <DataGrid

                  disableSelectionOnClick

                  columns={columns}
                  getRowId={(rows)=> rows.Product_ID}
                  rows={filtered} // data na pinasok yung data 
                  pageSize={10}
                  checkboxSelection={true} 

                  onSelectionModelChange={(ids) => {
                    setAdd(ids);
                  }}
                />
    
        </Grid>
        
      </Grid>

    
    </Grid> 
    

          
</Grid>


        
    </div>
  )
}

export default Products
