// Library components

import { Grid, 
        InputAdornment,
        Typography,
        Fab,
        IconButton
} from '@mui/material'
import { CTextField } from '../products/Products' 
import React, { useEffect, useState } from 'react'

// Icons
import SearchOutlinedIcon from '@mui/icons-material/SearchOutlined';
import AddIcon from '@mui/icons-material/Add';
import DeleteOutlineOutlinedIcon from '@mui/icons-material/DeleteOutlineOutlined';

// Routes
import { useNavigate } from 'react-router-dom';
import { DataGrid } from '@mui/x-data-grid';
import axios from 'axios';



const Invoice = () => {
// Navigation
let NavS = useNavigate();


// Column header
const columns = [

  // ID Column
  { field: 'Customer_ID', 
    headerName: 'ID', 
    width: 70 ,
    headerAlign: 'center',

    renderCell: (cellValues) => {
      return (
        <div
          style={{
            color: "black",
            fontSize: 18,
            width: "100%",
            textAlign: "center"
          }}
        >
          {cellValues.value}
        </div>
      );
    }
  },

  // ID Column
  { field: 'Customer_name', 
    headerName: 'name', 
    width: 200,
    headerClassName: 'super-app-theme--header',
    headerAlign: 'center', 

    renderCell: (cellValues) => {
      return (
        <div
          style={{
            color: "black",
            fontSize: 18,
            width: "100%",
            textAlign: "center"
          }}
        >
          {cellValues.value}
        </div>
      );
    }
  
  },

  { field: 'Customer_address', 
    headerName: 'Address', 
    width: 130,
    headerAlign: 'center', 

    renderCell: (cellValues) => {
      return (
        <div
          style={{
            color: "black",
            fontSize: 18,
            width: "100%",
            textAlign: "center"
          }}
        >
          {cellValues.value}
        </div>
      );
    }

  
  },

  { field: 'Customer_email', 
    headerName: 'email', 
    width: 130,
    headerAlign: 'center', 

    renderCell: (cellValues) => {
      return (
      <div
      style={{
        color: "black",
        fontSize: 18,
        width: "100%",
        textAlign: "center"
        }}
      >
        {cellValues.value}
        </div>
        );
    }
  },

  { field: 'Customer_cell', 
    headerName: 'Cellphone number', 
    width: 150,
    headerAlign: 'center', 

    renderCell: (cellValues) => {
      return (
        <div
          style={{
            color: "black",
            fontSize: 18,
            width: "100%",
            textAlign: "center"
          }}
        >
          {cellValues.value}
        </div>
      );
    }
  },

  { field: 'Customer_purchase', 
  headerName: 'product purchase', 
  width: 150,
  headerAlign: 'center', 

  renderCell: (cellValues) => {
    return (
      <div
        style={{
          color: "black",
          fontSize: 18,
          width: "100%",
          textAlign: "center"
        }}
      >
        {cellValues.value}
      </div>
    );
  }
},

{ field: 'Customer_date', 
headerName: 'date', 
width: 150,
headerAlign: 'center', 

renderCell: (cellValues) => {
  return (
    <div
      style={{
        color: "black",
        fontSize: 18,
        width: "100%",
        textAlign: "center"
      }}
    >
      {cellValues.value}
    </div>
  );
}
},

{ field: 'Customer_total', 
headerName: 'total', 
width: 130,
headerAlign: 'center', 

renderCell: (cellValues) => {
  return (
    <div
      style={{
        color: "black",
        fontSize: 18,
        width: "100%",
        textAlign: "center"
      }}
    >
      {cellValues.value}
    </div>
  );
}
},

{ field: 'Customer_payment', 
headerName: 'status', 
width: 130,
headerAlign: 'center', 

renderCell: (cellValues) => {
  return (
    <div
      style={{
        color: "black",
        fontSize: 18,
        width: "100%",
        textAlign: "center"
      }}
    >
      {cellValues.value}
    </div>
  );
}
},
  
// delete
  {
    field: 'action',
    headerName: 'Action',
    width: 130,
    headerAlign: 'center', 
    renderCell: () => {
      return (
        <div
          style={{
            width: "100%",
            textAlign: "center",
            // marginRight: '2'
          }}
        >
         

        <IconButton color="primary" aria-label="delete">
            <DeleteOutlineOutlinedIcon/>
        </IconButton>
         

        </div>


      );
    }
  }

];
//search data variable
const [search,setSearch] = useState("")

// Data 
const [Data,set] = useState()

// Filtering data 
const filtered = !search ? 
Data 
: Data.filter((person) => 
    person.Customer_name.toLowerCase().includes(search.toLowerCase())
  );

// Htttp get request
useEffect(() => {
  axios.get('http://localhost/REACTJS/cognate1%20api/CustomerdataAPI.php')
.then(res=>set(res.data))
.catch(err=>console.log(err)) 
})



  return (
    <div>
      {/* Whole Container */}
        <Grid container spacing={2}>

{/* Search bar container */}
          <Grid item md={9} xs={12}>

            {/* Para may padding */}
           <Grid 
           container 
           padding={5}
           direction="row"
           justifyContent="center"
           alignItems="flex-start">

             {/* Search bar */}
              <CTextField 
              variant='standard' 
              fullWidth 
              placeholder='Searchbar'
              value={search}
              onChange={e=>setSearch(e.target.value)}

              InputProps={{
                endAdornment: (
                  <InputAdornment position="start">
    
                    <SearchOutlinedIcon fontSize='large' />
                 
                  </InputAdornment>
                ),
              }}
              />
           </Grid>
    
          </Grid>

          {/* Conatainer button */}
          <Grid item md={3} xs={12}>

            {/* Container para may padding */}
            <Grid 
            container 
            padding={5}           
            direction="row"
            justifyContent="center"
            alignItems="flex-start">

              {/* Button add */}
              <Fab 
              color="primary" 
              variant='extended' 
              aria-label="add" 
              onClick={e=>NavS("/Mainpage/Invoice/Add")} 
              >
                <AddIcon />
                add data
              </Fab>

            </Grid>
            
          </Grid>



          {/* Container for table */}
          <Grid item md={12}>
            
            {/* Para may padding */}
            <Grid 
            container             
            direction="row"
            justifyContent="center"
            alignItems="flex-start"
            padding={5}>

              {/* pang size at background color */}
              <Grid 
              container
              style={{
                height:550 ,
                width:'100%',
                backgroundColor: 'White',
                border:'2px solid black',
                borderRadius: 3               
              }}
              >
                <DataGrid
                  columns={columns}
                getRowId={(rows)=> rows.Customer_ID}
                rows={filtered} // data na pinasok yung data 
                  
              pageSize={10}
              // onCellClick={delete_product}
                />

              </Grid>

            



            </Grid>

          </Grid>

        </Grid>
    </div>
  )
}

export default Invoice
